import androidx.room.TypeConverter
import com.example.danabala.domain.model.Grade

/**
 * Конверторы типов для Room базы данных
 */
class Converters {
    @TypeConverter
    fun fromGrade(grade: Grade): String = grade.name

    @TypeConverter
    fun toGrade(gradeName: String): Grade = Grade.valueOf(gradeName)
}

package com.example.danabala.data.local

import android.content.Context
import android.content.SharedPreferences
import dagger.hilt.android.qualifiers.ApplicationContext
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Менеджер для работы с SharedPreferences
 */
@Singleton
class PreferencesManager @Inject constructor(
    @ApplicationContext context: Context
) {
    private val prefs: SharedPreferences = context.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)

    var currentUserId: String?
        get() = prefs.getString(KEY_CURRENT_USER_ID, null)
        set(value) = prefs.edit().putString(KEY_CURRENT_USER_ID, value).apply()

    var isFirstLaunch: Boolean
        get() = prefs.getBoolean(KEY_FIRST_LAUNCH, true)
        set(value) = prefs.edit().putBoolean(KEY_FIRST_LAUNCH, value).apply()

    var soundEnabled: Boolean
        get() = prefs.getBoolean(KEY_SOUND_ENABLED, true)
        set(value) = prefs.edit().putBoolean(KEY_SOUND_ENABLED, value).apply()

    companion object {
        private const val PREFS_NAME = "danabala_prefs"
        private const val KEY_CURRENT_USER_ID = "current_user_id"
        private const val KEY_FIRST_LAUNCH = "first_launch"
        private const val KEY_SOUND_ENABLED = "sound_enabled"
    }
}

