@Dao
interface ExerciseResultDao {
    @Query("SELECT * FROM exercise_results WHERE userId = :userId ORDER BY completedAt DESC")
    fun getUserResults(userId: String): Flow<List<ExerciseResultEntity>>

    @Query("SELECT * FROM exercise_results WHERE userId = :userId AND exerciseId = :exerciseId ORDER BY completedAt DESC")
    fun getUserResultsForExercise(userId: String, exerciseId: String): Flow<List<ExerciseResultEntity>>

    @Insert
    suspend fun insertResult(result: ExerciseResultEntity)

    @Query("DELETE FROM exercise_results WHERE userId = :userId")
    suspend fun deleteUserResults(userId: String)
}

