package com.example.danabala.data.repository

import com.example.danabala.data.local.AppDatabase
import com.example.danabala.data.local.entity.toDomain
import com.example.danabala.data.local.entity.toEntity
import com.example.danabala.domain.model.Exercise
import com.example.danabala.domain.model.ExerciseResult
import com.example.danabala.domain.model.ExerciseType
import com.example.danabala.domain.model.Grade
import com.example.danabala.domain.repository.ExerciseRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flowOf
import kotlinx.coroutines.flow.map
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Реализация репозитория упражнений
 */
@Singleton
class ExerciseRepositoryImpl @Inject constructor(
    private val database: AppDatabase
) : ExerciseRepository {

    private val exerciseResultDao = database.exerciseResultDao()

    // Временно используем статические данные для упражнений
    // В будущем можно добавить загрузку с сервера или из локальных файлов
    private val staticExercises = listOf(
        Exercise(
            id = "math_add_1",
            title = "Сложение до 10",
            description = "Учимся складывать числа от 1 до 10",
            type = ExerciseType.MATH_ADDITION,
            difficulty = com.example.danabala.domain.model.Difficulty.EASY,
            targetGrades = listOf(Grade.PRESCHOOL_5_6, Grade.GRADE_1),
            estimatedDurationMinutes = 10
        ),
        Exercise(
            id = "reading_letters_1",
            title = "Изучаем буквы А-Я",
            description = "Знакомимся с русским алфавитом",
            type = ExerciseType.READING_LETTERS,
            difficulty = com.example.danabala.domain.model.Difficulty.EASY,
            targetGrades = listOf(Grade.PRESCHOOL_4_5, Grade.PRESCHOOL_5_6),
            estimatedDurationMinutes = 15
        )
    )

    override suspend fun getExercisesByType(type: ExerciseType): Flow<List<Exercise>> {
        return flowOf(staticExercises.filter { it.type == type })
    }

    override suspend fun getExercisesByGrade(grade: Grade): Flow<List<Exercise>> {
        return flowOf(staticExercises.filter { grade in it.targetGrades })
    }

    override suspend fun getExerciseById(id: String): Result<Exercise?> {
        return try {
            val exercise = staticExercises.find { it.id == id }
            Result.success(exercise)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun saveExerciseResult(result: ExerciseResult): Result<Unit> {
        return try {
            exerciseResultDao.insertResult(result.toEntity())
            Result.success(Unit)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun getUserResults(userId: String): Flow<List<ExerciseResult>> {
        return exerciseResultDao.getUserResults(userId).map { entities ->
            entities.map { it.toDomain() }
        }
    }

    override suspend fun getUserResultsForExercise(userId: String, exerciseId: String): Flow<List<ExerciseResult>> {
        return exerciseResultDao.getUserResultsForExercise(userId, exerciseId).map { entities ->
            entities.map { it.toDomain() }
        }
    }
}

package com.example.danabala.data.repository

import com.example.danabala.data.local.AppDatabase
import com.example.danabala.data.local.PreferencesManager
import com.example.danabala.data.local.entity.toDomain
import com.example.danabala.data.local.entity.toEntity
import com.example.danabala.domain.model.User
import com.example.danabala.domain.repository.UserRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Реализация репозитория пользователей
 */
@Singleton
class UserRepositoryImpl @Inject constructor(
    private val database: AppDatabase,
    private val preferencesManager: PreferencesManager
) : UserRepository {

    private val userDao = database.userDao()

    override suspend fun createUser(user: User): Result<Unit> {
        return try {
            userDao.insertUser(user.toEntity())
            Result.success(Unit)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun getUserById(id: String): Result<User?> {
        return try {
            val user = userDao.getUserById(id)?.toDomain()
            Result.success(user)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun getAllUsers(): Flow<List<User>> {
        return userDao.getAllUsers().map { entities ->
            entities.map { it.toDomain() }
        }
    }

    override suspend fun updateUser(user: User): Result<Unit> {
        return try {
            userDao.updateUser(user.toEntity())
            Result.success(Unit)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun deleteUser(id: String): Result<Unit> {
        return try {
            userDao.deleteUser(id)
            // Если удаляемый пользователь был текущим, сбрасываем настройку
            if (preferencesManager.currentUserId == id) {
                preferencesManager.currentUserId = null
            }
            Result.success(Unit)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    override suspend fun getCurrentUser(): Flow<User?> {
        return userDao.getAllUsers().map { users ->
            val currentUserId = preferencesManager.currentUserId
            users.find { it.id == currentUserId }?.toDomain()
        }
    }

    override suspend fun setCurrentUser(userId: String): Result<Unit> {
        return try {
            preferencesManager.currentUserId = userId
            Result.success(Unit)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }
}

package com.example.danabala.di

