class BubbleCatchActivity : AppCompatActivity(), TextToSpeech.OnInitListener {

    private lateinit var gameField: FrameLayout
    private lateinit var timerText: TextView
    private lateinit var scoreText: TextView
    private lateinit var targetText: TextView
    private lateinit var progressBar: ProgressBar

    private var score = 0
    private val totalLevels = 10
    private var currentLevel = 0
    private val digitsOrder = (0..9).shuffled().toMutableList()
    private var targetDigit = 0

    private var tts: TextToSpeech? = null
    private var levelTimer: CountDownTimer? = null
    private val handler = Handler(Looper.getMainLooper())
    private var spawnRunnable: Runnable? = null
    private val scheduledIncrements = mutableListOf<Runnable>()

    // Градиентный фон
    private lateinit var rootView: View
    private val gradientHandler = Handler(Looper.getMainLooper())
    private var gradientRunnable: Runnable? = null
    private var gradientIndex = 0
    private val gradientIds = intArrayOf(
        R.drawable.child_gradient_yellow,
        R.drawable.child_gradient_blue,
        R.drawable.child_gradient_purple
    )

    // Параметры игры
    private var currentConcurrentLimit: Int = 5
    private val maxConcurrentCapBase: Int = 15

    // Вероятность появления целевой цифры
    private val targetDigitProbability = 0.35f

    // Словесные названия чисел для TTS
    private val numberWords = arrayOf(
        "ноль", "один", "два", "три", "четыре", "пять",
        "шесть", "семь", "восемь", "девять"
    )

    // Короткие фразы для озвучивания событий
    private val correctHitPhrases = listOf(
        "Отлично!", "Верно!", "Правильно!", "Так держать!", "Молодец!"
    )
    private val wrongHitPhrases = listOf(
        "Не та цифра.", "Промах.", "Неверно.", "Попробуй ещё.", "Это не она."
    )
    private val missedPhrases = listOf(
        "Ты упустил нужный шарик.", "Нужный шарик улетел.", "Цель улетела.", "Будь внимательнее."
    )

    // Список активных шариков и апдейт-цикл
    private data class Bubble(
        val view: View,
        var x: Float,
        var y: Float,
        var vx: Float,
        var vy: Float,
        val radius: Float,
        val value: Int,
        var clickable: Boolean = true
    )
    private val bubbles = mutableListOf<Bubble>()
    private var updateRunnable: Runnable? = null
    private var lastUpdateMs: Long = 0L

    // Интервал спавна (уменьшается каждые 10 секунд уровня)
    private val baseSpawnIntervalMs = 900L
    private var currentSpawnIntervalMs = baseSpawnIntervalMs
    private val minSpawnIntervalMs = 300L
    private val scheduledSpawnReductions = mutableListOf<Runnable>()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_bubble_catch)
        requestedOrientation = ActivityInfo.SCREEN_ORIENTATION_PORTRAIT

        rootView = findViewById(R.id.main)
        ViewCompat.setOnApplyWindowInsetsListener(rootView) { v, insets ->
            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)
            insets
        }
        startGradientAnimation()

        tts = TextToSpeech(this, this)
        setupViews()
        setupBackButton()
    }

    // Градиент фона
    private fun startGradientAnimation() {
        stopGradientAnimation()
        fun nextIndex(i: Int) = (i + 1) % gradientIds.size
        gradientRunnable = object : Runnable {
            override fun run() {
                val curr = ContextCompat.getDrawable(this@BubbleCatchActivity, gradientIds[gradientIndex])
                val next = ContextCompat.getDrawable(this@BubbleCatchActivity, gradientIds[nextIndex(gradientIndex)])
                if (curr != null && next != null) {
                    val td = TransitionDrawable(arrayOf(curr, next))
                    rootView.background = td
                    td.isCrossFadeEnabled = true
                    td.startTransition(6000)
                }
                gradientIndex = nextIndex(gradientIndex)
                gradientHandler.postDelayed(this, 6000L)
            }
        }
        gradientHandler.post(gradientRunnable!!)
    }
    private fun stopGradientAnimation() {
        gradientRunnable?.let { gradientHandler.removeCallbacks(it) }
        gradientRunnable = null
    }

    private fun setupViews() {
        gameField = findViewById(R.id.gameField)
        timerText = findViewById(R.id.timerText)
        scoreText = findViewById(R.id.scoreText)
        targetText = findViewById(R.id.targetText)
        progressBar = findViewById(R.id.levelProgressBar)

        scoreText.text = score.toString()
        progressBar.max = LEVEL_DURATION_MS.toInt()
        progressBar.progress = LEVEL_DURATION_MS.toInt()

        findViewById<ImageView>(R.id.speakerButton).setOnClickListener { speakTarget() }
    }

    private fun setupBackButton() {
        findViewById<ImageView>(R.id.backButton).setOnClickListener { finish() }
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            tts?.language = Locale.forLanguageTag("ru-RU")
            startNextLevel()
        }
    }

    private fun startNextLevel() {
        if (currentLevel >= totalLevels) { finishGame(); return }

        // Настройка цели уровня
        targetDigit = digitsOrder[currentLevel]
        targetText.text = targetDigit.toString()
        speakTarget()

        // Таймер уровня
        levelTimer?.cancel()
        levelTimer = object : CountDownTimer(LEVEL_DURATION_MS, 100L) {
            override fun onTick(millisUntilFinished: Long) { updateTimerUi(millisUntilFinished) }
            override fun onFinish() {
                updateTimerUi(0)
                stopSpawning()
                clearAllBubbles()
                currentLevel++
                gameField.postDelayed({ startNextLevel() }, 600)
            }
        }.start()

        // Динамика одновременных шариков на уровне
        currentConcurrentLimit = 5
        scheduleConcurrencyIncrements()

        // Сброс и планирование уменьшения интервала спавна каждые 3 секунды
        currentSpawnIntervalMs = baseSpawnIntervalMs
        scheduleSpawnIntervalReductions()

        // Старт апдейт-цикла и спавна
        startUpdateLoop()
        startSpawning()
    }

    private fun updateTimerUi(millisUntilFinished: Long) {
        val seconds = (millisUntilFinished / 1000).toInt()
        timerText.text = String.format(Locale.getDefault(), "%02d:%02d", seconds / 60, seconds % 60)
        progressBar.progress = millisUntilFinished.toInt()
    }

    private fun scheduleConcurrencyIncrements() {
        cancelScheduledIncrements()
        val stepMs = 10_000L
        var delay = stepMs
        repeat(10) {
            val r = Runnable { currentConcurrentLimit = min(currentConcurrentLimit + 1, maxConcurrentCapBase) }
            scheduledIncrements += r
            handler.postDelayed(r, delay)
            delay += stepMs
        }
    }
    private fun cancelScheduledIncrements() {
        scheduledIncrements.forEach { handler.removeCallbacks(it) }
        scheduledIncrements.clear()
    }

    private fun scheduleSpawnIntervalReductions() {
        cancelScheduledSpawnReductions()
        var delay = 3_000L
        while (delay < LEVEL_DURATION_MS) {
            val r = Runnable {
                currentSpawnIntervalMs = (currentSpawnIntervalMs - 100L).coerceAtLeast(minSpawnIntervalMs)
            }
            scheduledSpawnReductions += r
            handler.postDelayed(r, delay)
            delay += 3_000L
        }
    }
    private fun cancelScheduledSpawnReductions() {
        if (scheduledSpawnReductions.isNotEmpty()) {
            scheduledSpawnReductions.forEach { handler.removeCallbacks(it) }
            scheduledSpawnReductions.clear()
        }
    }

    private fun startSpawning() {
        stopSpawning()
        spawnRunnable = object : Runnable {
            override fun run() {
                val active = bubbles.size
                if (active < currentConcurrentLimit) {
                    val deficit = currentConcurrentLimit - active
                    val batch = Random.nextInt(1, min(3, deficit) + 1)
                    spawnBubbles(batch)
                }
                handler.postDelayed(this, currentSpawnIntervalMs)
            }
        }
        handler.post(spawnRunnable!!)
    }
    private fun stopSpawning() {
        spawnRunnable?.let { handler.removeCallbacks(it) }
        spawnRunnable = null
        cancelScheduledIncrements()
        cancelScheduledSpawnReductions()
    }

    private fun startUpdateLoop() {
        if (updateRunnable != null) return
        lastUpdateMs = SystemClock.uptimeMillis()
        updateRunnable = object : Runnable {
            override fun run() {
                val now = SystemClock.uptimeMillis()
                val dt = ((now - lastUpdateMs).coerceAtMost(50)).toFloat() / 1000f
                lastUpdateMs = now
                stepPhysics(dt)
                handler.postDelayed(this, 16L)
            }
        }
        handler.post(updateRunnable!!)
    }
    private fun stopUpdateLoop() { updateRunnable?.let { handler.removeCallbacks(it) }; updateRunnable = null }

    private fun stepPhysics(dt: Float) {
        if (gameField.width == 0 || gameField.height == 0) return
        val width = gameField.width

        // Обновление позиций
        for (b in bubbles) {
            b.x += b.vx * dt
            b.y += b.vy * dt
            // столкновения со стенами (по X), небольшое затухание
            if (b.x < 0f) { b.x = 0f; b.vx = -b.vx * 0.9f }
            val maxX = (width - b.radius * 2)
            if (b.x > maxX) { b.x = maxX; b.vx = -b.vx * 0.9f }
        }

        // Столкновения между шарами (упругие, почти)
        for (i in 0 until bubbles.size) {
            val a = bubbles[i]
            for (j in i + 1 until bubbles.size) {
                val c = bubbles[j]
                val ax = a.x + a.radius
                val ay = a.y + a.radius
                val cx = c.x + c.radius
                val cy = c.y + c.radius
                val dx = cx - ax
                val dy = cy - ay
                val dist = hypot(dx, dy)
                val minDist = a.radius + c.radius + dpf(6f) // небольшой зазор
                if (dist < minDist && dist > 0f) {
                    // Раздвигаем на половины перекрытия
                    val overlap = (minDist - dist)
                    val nx = dx / dist
                    val ny = dy / dist
                    a.x -= nx * (overlap / 2f)
                    a.y -= ny * (overlap / 2f)
                    c.x += nx * (overlap / 2f)
                    c.y += ny * (overlap / 2f)
                    // Импульс по ��ормали (равные массы)
                    val rvx = c.vx - a.vx
                    val rvy = c.vy - a.vy
                    val vn = rvx * nx + rvy * ny
                    if (vn < 0f) {
                        val e = 0.8f // коэффициент реституции
                        val jImp = -(1 + e) * vn / 2f
                        val jx = jImp * nx
                        val jy = jImp * ny
                        a.vx -= jx; a.vy -= jy
                        c.vx += jx; c.vy += jy
                    }
                }
            }
        }

        // Удаление улетевших и обновление view
        val iterator = bubbles.iterator()
        while (iterator.hasNext()) {
            val b = iterator.next()
            if (b.y + b.radius * 2 < 0) {
                if (b.clickable && b.value == targetDigit) {
                    score -= 1
                    if (score < 0) {
                        score = 0
                        scoreText.text = "0"
                        tts?.speak(missedPhrases.random(), TextToSpeech.QUEUE_ADD, null, "missed")
                        triggerGameOver()
                        return
                    } else {
                        scoreText.text = score.toString()
                        tts?.speak(missedPhrases.random(), TextToSpeech.QUEUE_ADD, null, "missed")
                    }
                }
                (b.view.parent as? ViewGroup)?.removeView(b.view)
                iterator.remove()
                continue
            }
            b.view.x = b.x
            b.view.y = b.y
        }
    }

    private fun spawnBubbles(count: Int) {
        val sizes = IntArray(count) { dp(Random.nextInt(84, 126)) }
        gameField.post {
            val startYBase = gameField.height

            // Колонки по ширине
            val maxSizePx = dp(126)
            val laneGap = dp(12) // увеличенный зазор
            val laneWidth = max(maxSizePx + laneGap, 1)
            val lanes = floor(gameField.width.toFloat() / laneWidth).toInt().coerceAtLeast(1)
            val centers = FloatArray(lanes) { i -> (laneWidth / 2f) + i * laneWidth }

            repeat(count) { idx ->
                val size = sizes[idx]
                val startY = startYBase + size
                val endY = -size

                // Подбор X: по свободным колонкам, затем fallback
                val order = (0 until lanes).shuffled()
                var chosenX: Float? = null
                for (li in order) {
                    val cx = centers[li]
                    val xCand = (cx - size / 2f).coerceIn(0f, (gameField.width - size).toFloat())
                    if (!willOverlapStrict(xCand, startY.toFloat(), size)) { chosenX = xCand; break }
                }
                val x = chosenX ?: findFreeX(size, startY)

                // Создание view и модели
                val bubbleView = FrameLayout(this).apply {
                    layoutParams = FrameLayout.LayoutParams(size, size)
                    this.x = x
                    this.y = startY.toFloat()
                    background = createGlossyBubbleDrawable(randomBubbleColor(), size)
                }
                val value = if (Random.nextFloat() < targetDigitProbability) targetDigit else {
                    var v: Int; do { v = Random.nextInt(0, 10) } while (v == targetDigit); v
                }
                val text = TextView(this).apply {
                    layoutParams = FrameLayout.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT)
                    setTextSize(TypedValue.COMPLEX_UNIT_SP, 22f)
                    setTextColor(Color.WHITE)
                    gravity = Gravity.CENTER
                    setShadowLayer(4f, 0f, 2f, Color.parseColor("#66000000"))
                    text = value.toString()
                }
                bubbleView.addView(text)

                val radius = size / 2f
                // Базовая скорость по вертикали (px/с) ~ путь / 6с, с разбросом
                val travelDist = (startY - endY).toFloat()
                val baseVy = -travelDist / 6f
                val vy = baseVy * Random.nextDouble(0.85, 1.25).toFloat()
                val vx = dp(8) * Random.nextDouble(-0.6, 0.6).toFloat() // небольшой боковой дрейф

                val model = Bubble(bubbleView, x, startY.toFloat(), vx, vy, radius, value, true)
                bubbleView.isClickable = true
                bubbleView.setOnClickListener {
                    if (!model.clickable) return@setOnClickListener
                    if (model.value == targetDigit) {
                        score += 2
                        scoreText.text = score.toString()
                        tts?.speak(correctHitPhrases.random(), TextToSpeech.QUEUE_ADD, null, "hit_ok")
                        model.clickable = false
                        bubbles.remove(model)
                        animateCorrectBubbleAndRemove(bubbleView)
                    } else {
                        score -= 1
                        if (score < 0) {
                            score = 0
                            scoreText.text = "0"
                            bubbleView.performHapticFeedback(android.view.HapticFeedbackConstants.KEYBOARD_TAP)
                            animateWrongBubble(bubbleView)
                            tts?.speak(wrongHitPhrases.random(), TextToSpeech.QUEUE_ADD, null, "hit_bad")
                            triggerGameOver()
                        } else {
                            scoreText.text = score.toString()
                            bubbleView.performHapticFeedback(android.view.HapticFeedbackConstants.KEYBOARD_TAP)
                            animateWrongBubble(bubbleView)
                            tts?.speak(wrongHitPhrases.random(), TextToSpeech.QUEUE_ADD, null, "hit_bad")
                        }
                    }
                }

                gameField.addView(bubbleView)
                bubbles.add(model)
            }
        }
    }

    // Строгая проверка перекрытий: радиусы + гориз./верт. з��зоры
    private fun willOverlapStrict(xTopLeft: Float, yTopLeft: Float, size: Int): Boolean {
        val cx = xTopLeft + size / 2f
        val cy = yTopLeft + size / 2f
        val r = size / 2f
        val gap = dpf(12f)
        val minH = dpf(10f)
        val minV = dpf(10f)
        for (b in bubbles) {
            val bx = b.x + b.radius
            val by = b.y + b.radius
            val dx = abs(cx - bx)
            val dy = abs(cy - by)
            val centerDist = hypot(dx, dy)
            val radiusSum = r + b.radius + gap
            if (centerDist < radiusSum) return true
            if (dx < minH && dy < minV) return true
        }
        return false
    }

    private fun findFreeX(sizePx: Int, startY: Int): Float {
        val maxX = (gameField.width - sizePx).coerceAtLeast(0)
        repeat(16) {
            val candidate = Random.nextInt(0, maxX + 1)
            if (!willOverlapStrict(candidate.toFloat(), startY.toFloat(), sizePx)) return candidate.toFloat()
        }
        var x = 0
        while (x <= maxX) {
            if (!willOverlapStrict(x.toFloat(), startY.toFloat(), sizePx)) return x.toFloat()
            x += (sizePx / 2).coerceAtLeast(1)
        }
        return Random.nextInt(0, maxX + 1).toFloat()
    }

    private fun animateCorrectBubbleAndRemove(bubble: View) {
        bubble.isClickable = false
        val rotate = ObjectAnimator.ofFloat(bubble, View.ROTATION, 0f, 360f)
        val scaleX = ObjectAnimator.ofFloat(bubble, View.SCALE_X, 1f, 0f)
        val scaleY = ObjectAnimator.ofFloat(bubble, View.SCALE_Y, 1f, 0f)
        AnimatorSet().apply {
            duration = 400
            interpolator = AccelerateInterpolator()
            playTogether(rotate, scaleX, scaleY)
            addListener(object : AnimatorListenerAdapter() {
                override fun onAnimationEnd(animation: Animator) { (bubble.parent as? ViewGroup)?.removeView(bubble) }
            })
            start()
        }
    }

    private fun animateWrongBubble(bubble: View) {
        val sx = ObjectAnimator.ofFloat(bubble, View.SCALE_X, 1f, 0.9f, 1.06f, 1f)
        val sy = ObjectAnimator.ofFloat(bubble, View.SCALE_Y, 1f, 0.9f, 1.06f, 1f)
        AnimatorSet().apply {
            duration = 220
            interpolator = AccelerateDecelerateInterpolator()
            playTogether(sx, sy)
            start()
        }
    }

    private fun clearAllBubbles() {
        bubbles.clear()
        gameField.removeAllViews()
    }

    private fun speakTarget() {
        tts?.speak("Лови цифру ${numberWords[targetDigit]}", TextToSpeech.QUEUE_FLUSH, null, "catch_target")
    }

    private fun randomBubbleColor(): Int {
        val palette = listOf(
            Color.parseColor("#EF5350"),
            Color.parseColor("#AB47BC"),
            Color.parseColor("#5C6BC0"),
            Color.parseColor("#29B6F6"),
            Color.parseColor("#26A69A"),
            Color.parseColor("#66BB6A"),
            Color.parseColor("#FFCA28"),
            Color.parseColor("#FFA726"),
            Color.parseColor("#EC407A")
        )
        return palette[Random.nextInt(palette.size)]
    }

    private fun dp(value: Int): Int = TypedValue.applyDimension(
        TypedValue.COMPLEX_UNIT_DIP, value.toFloat(), resources.displayMetrics
    ).roundToInt()
    private fun dpf(value: Float): Float = TypedValue.applyDimension(
        TypedValue.COMPLEX_UNIT_DIP, value, resources.displayMetrics
    )

    private fun finishGame() {
        val intent = android.content.Intent(this, BubbleCatchResultsActivity::class.java)
        intent.putExtra("SCORE", score)
        startActivity(intent)
        finish()
    }

    private fun triggerGameOver() {
        stopSpawning()
        stopUpdateLoop()
        clearAllBubbles()
        val intent = android.content.Intent(this, BubbleCatchResultsActivity::class.java)
        intent.putExtra("SCORE", score)
        intent.putExtra("GAME_OVER", true)
        startActivity(intent)
        finish()
    }

    override fun onDestroy() {
        super.onDestroy()
        stopSpawning()
        stopUpdateLoop()
        levelTimer?.cancel()
        stopGradientAnimation()
        tts?.stop(); tts?.shutdown()
    }

    private fun createGlossyBubbleDrawable(baseColor: Int, sizePx: Int): Drawable {
        return GlossyBalloonDrawable(this, baseColor)
    }

    // Кастомный Drawable: сохраняет форму шара с хвостиком (ic_balloon) и накладывает объёмный градиент и блик
    private class GlossyBalloonDrawable(
        private val context: android.content.Context,
        private val baseColor: Int
    ) : Drawable() {
        private val rectF = RectF()
        private val bodyPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply { isDither = true }
        private val highlightPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply { isDither = true }
        private val xferAtop = PorterDuffXfermode(PorterDuff.Mode.SRC_ATOP)

        override fun draw(canvas: android.graphics.Canvas) {
            val b = bounds
            if (b.isEmpty) return
            rectF.set(b)

            // Подложка: исходный balloon с хвостиком, затонированный базовым цветом
            val balloon = ContextCompat.getDrawable(context, R.drawable.ic_balloon)?.mutate() ?: return
            balloon.setTint(baseColor)
            balloon.bounds = b

            // Градиент тела (радиальный, свет сверху-слева)
            val cx = b.left + b.width() * 0.35f
            val cy = b.top + b.height() * 0.35f
            val radius = max(b.width(), b.height()) * 0.75f
            bodyPaint.shader = RadialGradient(
                cx, cy, radius,
                intArrayOf(lighten(baseColor, 0.28f), baseColor, darken(baseColor, 0.22f)),
                floatArrayOf(0f, 0.65f, 1f),
                Shader.TileMode.CLAMP
            )

            // Блик сверху (линейный градиент)
            highlightPaint.shader = LinearGradient(
                b.left.toFloat(), b.top.toFloat(), b.left.toFloat(), b.top + b.height() * 0.7f,
                intArrayOf(Color.argb(160, 255, 255, 255), Color.argb(40, 255, 255, 255), Color.TRANSPARENT),
                floatArrayOf(0f, 0.6f, 1f),
                Shader.TileMode.CLAMP
            )

            // Слой: рисуем форму, затем накладываем градиенты по SRC_ATOP, чтобы сохранить силуэт (включая хвостик)
            val save = canvas.saveLayer(rectF, null)
            balloon.draw(canvas)

            bodyPaint.xfermode = xferAtop
            canvas.drawRect(rectF, bodyPaint)
            bodyPaint.xfermode = null

            highlightPaint.xfermode = xferAtop
            canvas.drawRect(rectF, highlightPaint)
            highlightPaint.xfermode = null

            canvas.restoreToCount(save)
        }

        override fun setAlpha(alpha: Int) {}
        override fun setColorFilter(colorFilter: android.graphics.ColorFilter?) {}
        override fun getOpacity(): Int = android.graphics.PixelFormat.TRANSLUCENT

        private fun lighten(color: Int, amount: Float): Int {
            val a = Color.alpha(color)
            val r = (Color.red(color) + (255 - Color.red(color)) * amount).toInt().coerceIn(0, 255)
            val g = (Color.green(color) + (255 - Color.green(color)) * amount).toInt().coerceIn(0, 255)
            val b = (Color.blue(color) + (255 - Color.blue(color)) * amount).toInt().coerceIn(0, 255)
            return Color.argb(a, r, g, b)
        }
        private fun darken(color: Int, amount: Float): Int {
            val a = Color.alpha(color)
            val r = (Color.red(color) * (1f - amount)).toInt().coerceIn(0, 255)
            val g = (Color.green(color) * (1f - amount)).toInt().coerceIn(0, 255)
            val b = (Color.blue(color) * (1f - amount)).toInt().coerceIn(0, 255)
            return Color.argb(a, r, g, b)
        }
    }

    companion object { private const val LEVEL_DURATION_MS = 60_000L }
}

package com.timursarsembayev.danabalanumbers

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.cardview.widget.CardView
import androidx.recyclerview.widget.RecyclerView

class AudioButtonsAdapter(
    private var items: MutableList<MatchingItem>,
    private val onItemClick: (MatchingItem, View) -> Unit
) : RecyclerView.Adapter<AudioButtonsAdapter.AudioButtonViewHolder>() {

    class AudioButtonViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val cardView: CardView = itemView.findViewById(R.id.cardView)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AudioButtonViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_audio_button, parent, false)
        return AudioButtonViewHolder(view)
    }

    override fun onBindViewHolder(holder: AudioButtonViewHolder, position: Int) {
        val item = items[position]

        // Устанавливаем прозрачность для сопоставленных элементов
        holder.cardView.alpha = if (item.isMatched) 0.3f else 1.0f

        holder.cardView.setOnClickListener {
            if (!item.isMatched) {
                // Простой эффект нажатия
                holder.cardView.animate().scaleX(0.95f).scaleY(0.95f).setDuration(100)
                    .withEndAction {
                        holder.cardView.animate().scaleX(1.0f).scaleY(1.0f).setDuration(100).start()
                    }.start()

                onItemClick(item, holder.cardView)
            }
        }
    }

    override fun getItemCount(): Int = items.size

    fun updateItems(newItems: List<MatchingItem>) {
        items.clear()
        items.addAll(newItems)
        notifyDataSetChanged()
    }

    fun removeItem(item: MatchingItem) {
        val position = items.indexOf(item)
        if (position != -1) {
            items.removeAt(position)
            notifyItemRemoved(position)
        }
    }
}

package com.timursarsembayev.danabalanumbers

import android.animation.Animator
import android.animation.AnimatorListenerAdapter
import android.animation.ValueAnimator
import android.content.Intent
import android.os.Bundle
import android.speech.tts.TextToSpeech
import android.view.View
import android.widget.ImageView
import android.widget.LinearLayout
import android.widget.ProgressBar
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import java.util.Locale
import kotlin.random.Random

class AudioMatchingActivity : AppCompatActivity(), TextToSpeech.OnInitListener {

    private lateinit var audioButtonsRecyclerView: RecyclerView
    private lateinit var numbersRecyclerView: RecyclerView
    private lateinit var audioButtonsAdapter: AudioButtonsAdapter
    private lateinit var numbersAdapter: NumbersAdapter
    private lateinit var progressBar: ProgressBar
    private lateinit var backButton: ImageView

    private var currentLevel = 1
    private var totalLevels = 10
    private var levels = mutableListOf<AudioMatchingLevel>()
    private var currentLevelData: AudioMatchingLevel? = null

    private var selectedAudioItem: MatchingItem? = null
    private var selectedNumberItem: MatchingItem? = null
    private var selectedAudioView: View? = null
    private var selectedNumberView: View? = null

    private var completedMatches = 0
    private var correctActions = 0
    private var incorrectActions = 0

    private lateinit var tts: TextToSpeech
    private var isTtsReady = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_audio_matching)

        initTTS()
        initViews()
        initData()
        setupRecyclerViews()
        loadLevel(currentLevel)
    }

    private fun initTTS() {
        tts = TextToSpeech(this, this)
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            val result = tts.setLanguage(Locale("ru"))
            isTtsReady = result != TextToSpeech.LANG_MISSING_DATA && result != TextToSpeech.LANG_NOT_SUPPORTED
        }
    }

    private fun speakNumber(number: Int) {
        if (isTtsReady) {
            val numberText = when (number) {
                1 -> "один"
                2 -> "два"
                3 -> "три"
                4 -> "четыре"
                5 -> "пять"
                6 -> "шесть"
                7 -> "семь"
                8 -> "восемь"
                9 -> "девять"
                else -> number.toString()
            }
            tts.speak(numberText, TextToSpeech.QUEUE_FLUSH, null, null)
        }
    }

    private fun speakText(text: String) {
        if (isTtsReady) {
            tts.speak(text, TextToSpeech.QUEUE_FLUSH, null, null)
        }
    }

    private fun initViews() {
        audioButtonsRecyclerView = findViewById(R.id.audioButtonsRecyclerView)
        numbersRecyclerView = findViewById(R.id.numbersRecyclerView)
        progressBar = findViewById(R.id.progressBar)
        backButton = findViewById(R.id.backButton)

        backButton.setOnClickListener {
            finish()
        }
    }

    private fun initData() {
        levels = AudioMatchingGameData.generateAllLevels().toMutableList()
    }

    private fun setupRecyclerViews() {
        audioButtonsAdapter = AudioButtonsAdapter(mutableListOf()) { item, view ->
            onAudioButtonClick(item, view)
        }

        numbersAdapter = NumbersAdapter(mutableListOf()) { item, view ->
            onNumberItemClick(item, view)
        }

        audioButtonsRecyclerView.apply {
            layoutManager = LinearLayoutManager(this@AudioMatchingActivity)
            adapter = audioButtonsAdapter
        }

        numbersRecyclerView.apply {
            layoutManager = LinearLayoutManager(this@AudioMatchingActivity)
            adapter = numbersAdapter
        }
    }

    private fun loadLevel(level: Int) {
        currentLevelData = levels[level - 1]
        updateProgressBar()
        completedMatches = 0

        // Перемешиваем элементы для случайного порядка
        val audioButtons = currentLevelData!!.pairs.map { it.number }.shuffled()
        val numbers = currentLevelData!!.pairs.map { it.number }.shuffled()

        audioButtonsAdapter.updateItems(audioButtons)
        numbersAdapter.updateItems(numbers)

        clearSelections()
        resetViewsAlpha()
    }

    private fun updateProgressBar() {
        val progress = (currentLevel * 100) / totalLevels
        progressBar.progress = progress
    }

    private fun resetViewsAlpha() {
        audioButtonsRecyclerView.post {
            for (i in 0 until audioButtonsRecyclerView.childCount) {
                val child = audioButtonsRecyclerView.getChildAt(i)
                child.alpha = 1.0f
                child.scaleX = 1.0f
                child.scaleY = 1.0f
            }
        }

        numbersRecyclerView.post {
            for (i in 0 until numbersRecyclerView.childCount) {
                val child = numbersRecyclerView.getChildAt(i)
                child.alpha = 1.0f
                child.scaleX = 1.0f
                child.scaleY = 1.0f
            }
        }
    }

    private fun onAudioButtonClick(item: MatchingItem, view: View) {
        if (item.isMatched) return

        // Озвучиваем число
        speakNumber(item.value)

        // Убираем предыдущее выделение
        selectedAudioView?.let { prevView ->
            val linearLayout = prevView.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.white))
        }

        if (selectedAudioItem == item) {
            clearSelections()
            return
        }

        selectedAudioItem = item
        selectedAudioView = view

        // Выделяем элемент зеленым цветом
        val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
        linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))

        checkMatch()
    }

    private fun onNumberItemClick(item: MatchingItem, view: View) {
        if (item.isMatched) return

        // Убираем предыдущее выделение
        selectedNumberView?.let { prevView ->
            val numberText = prevView.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, R.color.button_color))
        }

        if (selectedNumberItem == item) {
            clearSelections()
            return
        }

        selectedNumberItem = item
        selectedNumberView = view

        // Выделяем элемент зеленым цветом
        val numberText = view.findViewById<TextView>(R.id.numberText)
        numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))

        checkMatch()
    }

    private fun checkMatch() {
        val audioItem = selectedAudioItem
        val numberItem = selectedNumberItem

        if (audioItem != null && numberItem != null) {
            if (audioItem.value == numberItem.value) {
                onCorrectMatch(audioItem, numberItem)
            } else {
                onIncorrectMatch()
            }
        }
    }

    private fun onCorrectMatch(audioItem: MatchingItem, numberItem: MatchingItem) {
        correctActions++

        val randomPhrase = MatchingFeedbackPhrases.correctPhrases[Random.nextInt(MatchingFeedbackPhrases.correctPhrases.size)]
        speakText(randomPhrase)

        audioItem.isMatched = true
        numberItem.isMatched = true

        animateMatch(selectedAudioView!!, selectedNumberView!!) {
            audioButtonsAdapter.removeItem(audioItem)
            numbersAdapter.removeItem(numberItem)

            completedMatches++

            if (completedMatches >= 5) {
                onLevelCompleted()
            }
        }

        clearSelections()
    }

    private fun onIncorrectMatch() {
        incorrectActions++

        val randomPhrase = MatchingFeedbackPhrases.incorrectPhrases[Random.nextInt(MatchingFeedbackPhrases.incorrectPhrases.size)]
        speakText(randomPhrase)

        // Выделяем обе карточки красным цветом на полсекунды
        selectedAudioView?.let { view ->
            val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_red_light))
        }

        selectedNumberView?.let { view ->
            val numberText = view.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_red_light))
        }

        // Через 500ms возвращаем зеленое выделение
        android.os.Handler(android.os.Looper.getMainLooper()).postDelayed({
            selectedAudioView?.let { view ->
                val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
                linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))
            }

            selectedNumberView?.let { view ->
                val numberText = view.findViewById<TextView>(R.id.numberText)
                numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))
            }
        }, 500)

        animateIncorrectMatch(selectedAudioView!!, selectedNumberView!!)
    }

    private fun animateMatch(view1: View, view2: View, onComplete: () -> Unit) {
        val animator1 = ValueAnimator.ofFloat(1f, 0f)
        val animator2 = ValueAnimator.ofFloat(1f, 0f)

        animator1.duration = 300
        animator2.duration = 300

        animator1.addUpdateListener { animation ->
            val alpha = animation.animatedValue as Float
            view1.alpha = alpha
            view1.scaleX = alpha
            view1.scaleY = alpha
        }

        animator2.addUpdateListener { animation ->
            val alpha = animation.animatedValue as Float
            view2.alpha = alpha
            view2.scaleX = alpha
            view2.scaleY = alpha
        }

        animator1.addListener(object : AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: Animator) {
                onComplete()
            }
        })

        animator1.start()
        animator2.start()
    }

    private fun animateIncorrectMatch(view1: View, view2: View) {
        val shake = ValueAnimator.ofFloat(0f, 25f, -25f, 25f, -25f, 15f, -15f, 6f, -6f, 0f)
        shake.duration = 600

        shake.addUpdateListener { animation ->
            val translateX = animation.animatedValue as Float
            view1.translationX = translateX
            view2.translationX = translateX
        }

        shake.addListener(object : AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: Animator) {
                view1.translationX = 0f
                view2.translationX = 0f
            }
        })

        shake.start()
    }

    private fun clearSelections() {
        selectedAudioItem = null
        selectedNumberItem = null

        selectedAudioView?.let { view ->
            val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.white))
        }

        selectedNumberView?.let { view ->
            val numberText = view.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, R.color.button_color))
        }

        selectedAudioView = null
        selectedNumberView = null
    }

    private fun onLevelCompleted() {
        if (currentLevel < totalLevels) {
            currentLevel++
            loadLevel(currentLevel)
        } else {
            showResults()
        }
    }

    private fun showResults() {
        val finalScore = (correctActions * 10) - (incorrectActions * 5)

        val intent = Intent(this, AudioMatchingResultsActivity::class.java)
        intent.putExtra("completed_levels", currentLevel)
        intent.putExtra("correct_actions", correctActions)
        intent.putExtra("incorrect_actions", incorrectActions)
        intent.putExtra("final_score", finalScore)
        startActivity(intent)
        finish()
    }

    override fun onDestroy() {
        if (::tts.isInitialized) {
            tts.stop()
            tts.shutdown()
        }
        super.onDestroy()
    }
}

package com.timursarsembayev.danabalanumbers

data class AudioMatchingLevel(
    val pairs: List<AudioMatchingPair>
)

data class AudioMatchingPair(
    val number: MatchingItem // Используем ту же модель, что и в обычном сопоставлении
)

object AudioMatchingGameData {
    fun generateAllLevels(): List<AudioMatchingLevel> {
        val levels = mutableListOf<AudioMatchingLevel>()

        // Создаем 10 уровней
        repeat(10) { levelIndex ->
            val pairs = mutableListOf<AudioMatchingPair>()

            // Генерируем 5 пар чисел для каждого уровня
            val usedNumbers = mutableSetOf<Int>()
            repeat(5) {
                var number: Int
                do {
                    number = (1..9).random()
                } while (usedNumbers.contains(number))

                usedNumbers.add(number)

                val matchingItem = MatchingItem(
                    value = number,
                    emoji = "", // Для аудио кнопок не нужен emoji
                    isMatched = false
                )

                pairs.add(AudioMatchingPair(matchingItem))
            }

            levels.add(AudioMatchingLevel(pairs))
        }

        return levels
    }
}

package com.timursarsembayev.danabalanumbers

import android.content.Intent
import android.os.Bundle
import android.speech.tts.TextToSpeech
import android.widget.ImageView
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import androidx.cardview.widget.CardView
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import java.util.*
import kotlin.random.Random

class AudioMatchingResultsActivity : AppCompatActivity(), TextToSpeech.OnInitListener {

    private var tts: TextToSpeech? = null
    private var isTtsReady = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_audio_matching_results)

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->
            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)
            insets
        }

        // Инициализация TTS
        tts = TextToSpeech(this, this)

        setupViews()
        setupButtons()
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            val result = tts?.setLanguage(Locale("ru"))
            isTtsReady = result != TextToSpeech.LANG_MISSING_DATA && result != TextToSpeech.LANG_NOT_SUPPORTED

            if (isTtsReady) {
                speakCongratulation()
            }
        }
    }

    private fun setupViews() {
        val completedLevels = intent.getIntExtra("completed_levels", 10)
        val correctActions = intent.getIntExtra("correct_actions", 0)
        val incorrectActions = intent.getIntExtra("incorrect_actions", 0)
        val finalScore = intent.getIntExtra("final_score", 0)

        // Обновляем UI с результатами
        findViewById<TextView>(R.id.completedLevelsText).text = "Пройдено уровней: $completedLevels"
        findViewById<TextView>(R.id.correctActionsText).text = "Правильных действий: $correctActions"
        findViewById<TextView>(R.id.incorrectActionsText).text = "Ошибок: $incorrectActions"
        findViewById<TextView>(R.id.finalScoreText).text = "Итоговые очки: $finalScore"

        // Определяем сообщение в зависимости от результата
        val accuracy = if (correctActions + incorrectActions > 0) {
            (correctActions * 100) / (correctActions + incorrectActions)
        } else {
            100
        }

        val (message, encouragement) = when {
            accuracy >= 90 -> Pair("Превосходно! ", "Ты отлично слышишь и различаешь числа!")
            accuracy >= 70 -> Pair("Отлично! ", "Ты хорошо сопоставляешь звуки с цифрами!")
            accuracy >= 50 -> Pair("Хорошо! ", "Продолжай тренироваться!")
            else -> Pair("Не сдавайся! ", "С каждым разом будет лучше!")
        }

        findViewById<TextView>(R.id.resultMessage).text = message
        findViewById<TextView>(R.id.encouragementText).text = encouragement
    }

    private fun speakCongratulation() {
        if (isTtsReady) {
            val correctActions = intent.getIntExtra("correct_actions", 0)
            val incorrectActions = intent.getIntExtra("incorrect_actions", 0)

            val accuracy = if (correctActions + incorrectActions > 0) {
                (correctActions * 100) / (correctActions + incorrectActions)
            } else {
                100
            }

            val phrases = when {
                accuracy == 100 -> DifferentiatedCongratulationPhrases.perfect100Phrases
                accuracy >= 90 -> DifferentiatedCongratulationPhrases.excellent90Phrases
                accuracy >= 80 -> DifferentiatedCongratulationPhrases.good80Phrases
                else -> DifferentiatedCongratulationPhrases.encouragement80Phrases
            }

            val randomPhrase = phrases[Random.nextInt(phrases.size)]
            tts?.speak(randomPhrase, TextToSpeech.QUEUE_FLUSH, null, "congratulation")
        }
    }

    private fun setupButtons() {
        findViewById<CardView>(R.id.btnPlayAgain).setOnClickListener {
            stopTTS()
            val intent = Intent(this, AudioMatchingActivity::class.java)
            startActivity(intent)
            finish()
        }

        findViewById<CardView>(R.id.btnBackToMenu).setOnClickListener {
            stopTTS()
            val intent = Intent(this, MathExercisesActivity::class.java)
            startActivity(intent)
            finish()
        }

        findViewById<ImageView>(R.id.btnBack).setOnClickListener {
            stopTTS()
            finish()
        }
    }

    private fun stopTTS() {
        tts?.stop()
    }

    override fun onDestroy() {
        tts?.stop()
        tts?.shutdown()
        super.onDestroy()
    }
}
