        "К" to "КА",
        "Л" to "ЭЛЬ",
        "М" to "ЭМ",
        "Н" to "ЭН",
        "О" to "ОООО",
        "П" to "ПЭ",
        "Р" to "ЭР",
        "С" to "ЭС",
        "Т" to "ТЭ",
        "У" to "У",
        "Ф" to "ЭФ",
        "Х" to "ХА",
        "Ц" to "ЦЭ",
        "Ч" to "ЧЭ",
        "Ш" to "ША",
        "Щ" to "ЩА",
        "Ъ" to "ТВЁРДЫЙ ЗНАК",
        "Ы" to "Ы",
        "Ь" to "МЯГКИЙ ЗНАК",
        "Э" to "Э",
        "Ю" to "Ю",
        "Я" to "Я"
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_letter_recognition)

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->
            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)
            insets
        }

        // Инициализация списка доступных букв
        initializeAvailableLetters()

        // Инициализация TTS
        tts = TextToSpeech(this, this)

        setupBackButton()
        setupSpeakerButton()

        // Убираем текст вопроса - делаем его невидимым
        findViewById<TextView>(R.id.questionText).visibility = View.GONE

        startNewQuestion()
    }

    private fun initializeAvailableLetters() {
        availableLetters.clear()
        availableLetters.addAll(russianAlphabet)
        availableLetters.shuffle()
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            val result = tts?.setLanguage(Locale("ru"))
            if (result == TextToSpeech.LANG_MISSING_DATA || result == TextToSpeech.LANG_NOT_SUPPORTED) {
                Log.e("TTS", "Русский язык не поддерживается, используем английский")
                tts?.language = Locale.getDefault()
            }
            isTtsReady = true

            // Озвучиваем вопрос сразу, если он уже готов
            if (currentQuestionText.isNotEmpty()) {
                // Небольшая задержка для первого вопроса
                handler.postDelayed({
                    speakQuestion()
                }, 500)
            }
        } else {
            Log.e("TTS", "Инициализация TTS не удалась")
        }
    }

    private fun setupBackButton() {
        findViewById<View>(R.id.backButton).setOnClickListener {
            finish()
        }
    }

    private fun setupSpeakerButton() {
        findViewById<ImageView>(R.id.speakerButton).setOnClickListener {
            speakQuestion()
        }
    }

    private fun speakQuestion() {
        if (isTtsReady && currentQuestionText.isNotEmpty()) {
            tts?.speak(currentQuestionText, TextToSpeech.QUEUE_FLUSH, null, "question")
        }
    }

    private fun vibrateOnError() {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
            val vibratorManager = getSystemService(Context.VIBRATOR_MANAGER_SERVICE) as VibratorManager
            val vibrator = vibratorManager.defaultVibrator
            vibrator.vibrate(VibrationEffect.createOneShot(200, VibrationEffect.DEFAULT_AMPLITUDE))
        } else {
            @Suppress("DEPRECATION")
            val vibrator = getSystemService(Context.VIBRATOR_SERVICE) as Vibrator
            if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                vibrator.vibrate(VibrationEffect.createOneShot(200, VibrationEffect.DEFAULT_AMPLITUDE))
            } else {
                @Suppress("DEPRECATION")
                vibrator.vibrate(200)
            }
        }
    }

    private fun startNewQuestion() {
        if (currentQuestion >= totalQuestions) {
            showResultsScreen()
            return
        }

        currentQuestion++
        currentQuestionHasError = false

        // Получаем следующую букву из списка доступных
        if (availableLetters.isEmpty()) {
            initializeAvailableLetters()
        }
        targetLetter = availableLetters.removeAt(0)

        // Обновляем вопрос с правильным произношением
        val letterName = letterPronunciation[targetLetter] ?: targetLetter
        val questionText = "Найди букву $letterName"
        currentQuestionText = questionText

        // Озвучиваем вопрос (если TTS готов)
        if (isTtsReady) {
            speakQuestion()
        }

        // Обновляем прогресс-бар
        val progressBar = findViewById<ProgressBar>(R.id.progressBar)
        progressBar.progress = (currentQuestion * 100) / totalQuestions

        // Генерируем 4 случайные буквы, одна из которых правильная
        val letters = generateLetterOptions(targetLetter)

        val cards = listOf(
            findViewById<CardView>(R.id.card1),
            findViewById<CardView>(R.id.card2),
            findViewById<CardView>(R.id.card3),
            findViewById<CardView>(R.id.card4)
        )

        val letterTexts = listOf(
            findViewById<TextView>(R.id.letter1),
            findViewById<TextView>(R.id.letter2),
            findViewById<TextView>(R.id.letter3),
            findViewById<TextView>(R.id.letter4)
        )

        // Заполняем карточки
        for (i in 0..3) {
            letterTexts[i].text = letters[i]
            cards[i].setCardBackgroundColor(ContextCompat.getColor(this, android.R.color.white))
            cards[i].isClickable = true
            animateCardEntrance(cards[i], i * 100L)

            cards[i].setOnClickListener {
                checkAnswer(letters[i], cards[i])
            }
        }
    }

    private fun animateCardEntrance(card: CardView, delay: Long) {
        card.alpha = 0f
        card.scaleX = 0.5f
        card.scaleY = 0.5f

        card.animate()
            .alpha(1f)
            .scaleX(1f)
            .scaleY(1f)
            .setDuration(300)
            .setStartDelay(delay)
            .setInterpolator(BounceInterpolator())
            .start()
    }

    private fun generateLetterOptions(correct: String): List<String> {
        val options = mutableSetOf<String>()
        options.add(correct)

        while (options.size < 4) {
            val randomLetter = russianAlphabet[Random.nextInt(russianAlphabet.size)]
            options.add(randomLetter)
        }

        return options.shuffled()
    }

    private fun checkAnswer(selectedLetter: String, selectedCard: CardView) {
        if (selectedLetter == targetLetter) {
            // Правильный ответ
            if (currentQuestionHasError) {
                answersWithErrors++
            } else {
                correctAnswersCount++
            }

            animateCorrectAnswer(selectedCard)
            tts?.speak("Молодец!", TextToSpeech.QUEUE_FLUSH, null, "correct")
            disableAllCards()

            handler.postDelayed({
                startNewQuestion()
            }, 2000)
        } else {
            // Неправильный ответ
            currentQuestionHasError = true
            animateWrongAnswer(selectedCard)
            vibrateOnError()
            tts?.speak("Попробуй ещё раз, у тебя получится!", TextToSpeech.QUEUE_FLUSH, null, "wrong")
            selectedCard.isClickable = false
        }
    }

    private fun disableAllCards() {
        val cards = listOf(
            findViewById<CardView>(R.id.card1),
            findViewById<CardView>(R.id.card2),
            findViewById<CardView>(R.id.card3),
            findViewById<CardView>(R.id.card4)
        )
        cards.forEach { it.isClickable = false }
    }

    private fun animateCorrectAnswer(card: CardView) {
        card.setCardBackgroundColor(ContextCompat.getColor(this, android.R.color.holo_green_light))

        val scaleUpX = ObjectAnimator.ofFloat(card, "scaleX", 1f, 1.3f)
        val scaleUpY = ObjectAnimator.ofFloat(card, "scaleY", 1f, 1.3f)
        val scaleDownX = ObjectAnimator.ofFloat(card, "scaleX", 1.3f, 1f)
        val scaleDownY = ObjectAnimator.ofFloat(card, "scaleY", 1.3f, 1f)

        val animatorSet = AnimatorSet()
        animatorSet.play(scaleUpX).with(scaleUpY)
        animatorSet.play(scaleDownX).with(scaleDownY).after(scaleUpX)
        animatorSet.duration = 200
        animatorSet.start()
    }

    private fun animateWrongAnswer(card: CardView) {
        card.setCardBackgroundColor(ContextCompat.getColor(this, android.R.color.holo_red_light))

        val shake = ObjectAnimator.ofFloat(card, "translationX", 0f, 25f, -25f, 25f, -25f, 15f, -15f, 6f, -6f, 0f)
        shake.duration = 500
        shake.start()
    }

    private fun showResultsScreen() {
        val intent = Intent(this, ResultsActivity::class.java)
        intent.putExtra("score", correctAnswersCount)
        intent.putExtra("total", totalQuestions)
        intent.putExtra("correctAnswersCount", correctAnswersCount)
        intent.putExtra("answersWithErrors", answersWithErrors)
        startActivity(intent)
        finish()
    }

    override fun onDestroy() {
        tts?.stop()
        tts?.shutdown()
        super.onDestroy()
    }
}

