/**
 * DI модуль для связывания интерфейсов репозиториев с их реализациями
 */
@Module
@InstallIn(SingletonComponent::class)
abstract class RepositoryModule {

    @Binds
    @Singleton
    abstract fun bindUserRepository(userRepositoryImpl: UserRepositoryImpl): UserRepository

    @Binds
    @Singleton
    abstract fun bindExerciseRepository(exerciseRepositoryImpl: ExerciseRepositoryImpl): ExerciseRepository
}

package com.example.danabala.domain.model
/** * Базовая модель тренировки */ data class Exercise( val id: String, val title: String, val description: String, val type: ExerciseType, val difficulty: Difficulty, val targetGrades: List<Grade>, val estimatedDurationMinutes: Int, val iconResId: Int? = null )
/** * Типы тренировок */ enum class ExerciseType { MATH_ADDITION, // Сложение MATH_SUBTRACTION, // Вычитание MATH_MULTIPLICATION,// Умножение MATH_DIVISION, // Деление MATH_COUNTING, // Счет READING_LETTERS, // Изучение букв READING_SYLLABLES, // Слоги READING_WORDS, // Слова READING_SENTENCES, // Предложения READING_COMPREHENSION // Понимание прочитанного }
/** * Уровень сложности */ enum class Difficulty { EASY, MEDIUM, HARD }
/** * Результат выполнения тренировки */ data class ExerciseResult( val exerciseId: String, val userId: String, val score: Int, val maxScore: Int, val timeSpentSeconds: Int, val completedAt: Long = System.currentTimeMillis(), val mistakes: List<String> = emptyList() ) { val percentage: Float get() = if (maxScore > 0) (score.toFloat() / maxScore) * 100 else 0f }

package com.example.danabala.domain.model
/** * Базовая модель пользователя (ребенка) */ data class User( val id: String, val name: String, val age: Int, val grade: Grade, val avatarUrl: String? = null, val createdAt: Long = System.currentTimeMillis() )
/** * Класс обучения (дошкольник/школьник) */ enum class Grade { PRESCHOOL_3_4, // 3-4 года PRESCHOOL_4_5, // 4-5 лет PRESCHOOL_5_6, // 5-6 лет GRADE_1, // 1 класс GRADE_2, // 2 класс GRADE_3, // 3 класс GRADE_4 // 4 класс }

