package com.timursarsembayev.danabalanumbers

import android.os.Bundle
import android.view.View
import android.widget.ImageButton
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import com.google.android.material.button.MaterialButton
import com.timursarsembayev.danabalanumbers.R

class SudokuKidsActivity : AppCompatActivity() {
    private lateinit var view: SudokuKidsView
    private lateinit var levelView: TextView
    private lateinit var sizeView: TextView
    private lateinit var nextButton: MaterialButton

    private var level: Int = 1

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_sudoku_kids)

        view = findViewById<SudokuKidsView>(R.id.sudokuKidsView)
        levelView = findViewById<TextView>(R.id.textLevel)
        sizeView = findViewById<TextView>(R.id.textSize)
        nextButton = findViewById(R.id.buttonNextLevel)

        findViewById<ImageButton>(R.id.buttonBack).setOnClickListener { finish() }

        nextButton.setOnClickListener {
            // Подтверждение перехода
            level++
            view.startNewGame(gridSizeForLevel(level))
            updateUi()
            nextButton.visibility = View.GONE
        }

        updateUi()
        view.onSolved = {
            // Показываем кнопку подтверждения
            nextButton.visibility = View.VISIBLE
        }
        // Запуск первого уровня
        view.startNewGame(gridSizeForLevel(level))
        nextButton.visibility = View.GONE
    }

    private fun updateUi() {
        levelView.text = level.toString()
        sizeView.text = "${view.gridSize}×${view.gridSize}"
    }

    private fun gridSizeForLevel(lvl: Int): Int {
        val sizes = intArrayOf(3, 4, 6, 8, 12, 16, 24)
        val idx = ((lvl - 1) / 5).coerceAtMost(sizes.lastIndex)
        return sizes[idx]
    }
}

package com.timursarsembayev.danabalanumbers

import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.view.View
import android.widget.ImageButton
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import com.google.android.material.button.MaterialButton
import com.timursarsembayev.danabalanumbers.R
import java.util.Locale

class SudokuKidsActivity : AppCompatActivity() {
    private lateinit var view: SudokuKidsView
    private lateinit var levelView: TextView
    private lateinit var sizeView: TextView
    private lateinit var nextButton: MaterialButton
    private lateinit var timerView: TextView

    private var level: Int = 1

    // Таймер
    private val handler = Handler(Looper.getMainLooper())
    private var running = false
    private var startTimeMs = 0L

    private val updateTimer = object : Runnable {
        override fun run() {
            if (!running) return
            val elapsed = System.currentTimeMillis() - startTimeMs
            timerView.text = formatTime(elapsed)
            handler.postDelayed(this, 100)
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_sudoku_kids)

        view = findViewById<SudokuKidsView>(R.id.sudokuKidsView)
        levelView = findViewById<TextView>(R.id.textLevel)
        sizeView = findViewById<TextView>(R.id.textSize)
        nextButton = findViewById(R.id.buttonNextLevel)
        timerView = findViewById(R.id.timerView)

        findViewById<ImageButton>(R.id.buttonBack).setOnClickListener { finish() }

        nextButton.setOnClickListener {
            // Подтверждение перехода
            level++
            view.startNewGame(gridSizeForLevel(level))
            updateUi()
            nextButton.visibility = View.GONE
            restartTimer()
        }

        updateUi()
        view.onSolved = {
            // Показываем кнопку подтверждения и останавливаем таймер
            nextButton.visibility = View.VISIBLE
            stopTimer()
        }
        // Запуск первого уровня
        view.startNewGame(gridSizeForLevel(level))
        nextButton.visibility = View.GONE
        restartTimer()
    }

    override fun onPause() {
        super.onPause()
        stopTimer()
    }

    override fun onResume() {
        super.onResume()
        // Возобновляем таймер, если уровень ещё не решён и кнопка скрыта
        if (nextButton.visibility != View.VISIBLE) startTimerIfNeeded()
    }

    private fun updateUi() {
        levelView.text = level.toString()
        sizeView.text = "${view.gridSize}×${view.gridSize}"
    }

    private fun gridSizeForLevel(lvl: Int): Int {
        val sizes = intArrayOf(3, 4, 6, 8, 9)
        val idx = ((lvl - 1) / 10).coerceAtMost(sizes.lastIndex)
        return sizes[idx]
    }

    private fun restartTimer() {
        startTimeMs = System.currentTimeMillis()
        running = true
        handler.removeCallbacks(updateTimer)
        handler.post(updateTimer)
        timerView.text = formatTime(0)
    }

    private fun stopTimer() {
        running = false
        handler.removeCallbacks(updateTimer)
    }

    private fun startTimerIfNeeded() {
        if (!running) {
            startTimeMs = System.currentTimeMillis()
            running = true
            handler.post(updateTimer)
        }
    }

    private fun formatTime(ms: Long): String {
        val totalSec = ms / 1000
        val min = totalSec / 60
        val sec = totalSec % 60
        val hund = (ms % 1000) / 10
        return String.format(Locale.getDefault(), "%02d:%02d.%02d", min, sec, hund)
    }
}

package com.timursarsembayev.danabalanumbers

import android.content.Context
import android.graphics.*
import android.util.AttributeSet
import android.view.MotionEvent
import android.view.View
import android.animation.ValueAnimator
import android.view.animation.AccelerateDecelerateInterpolator
import androidx.core.animation.doOnEnd
import kotlin.math.min
import kotlin.math.roundToInt
import kotlin.math.abs
import kotlin.math.cos

class SudokuKidsView @JvmOverloads constructor(
    context: Context,
    attrs: AttributeSet? = null,
    defStyleAttr: Int = 0
) : View(context, attrs, defStyleAttr) {

    var onSolved: (() -> Unit)? = null

    var gridSize: Int = 3
        private set

    private var cellSize = 0f
    private var gridLeft = 0f
    private var gridTop = 0f

    private lateinit var board: Array<IntArray>
    private lateinit var conflictsRow: Array<BooleanArray>
    private lateinit var conflictsCol: Array<BooleanArray>

    // Анимация переворота для одной клетки за раз
    private var flipping = false
    private var flipR = -1
    private var flipC = -1
    private var flipPrevValue = 0
    private var flipProgress = 0f // 0..1
    private var flipAnimator: ValueAnimator? = null

    private val thinPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#33000000")
        strokeWidth = 1f * resources.displayMetrics.density
    }
    private val boldPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#66000000")
        strokeWidth = 3f * resources.displayMetrics.density
    }

    private val textPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        color = Color.WHITE
        textAlign = Paint.Align.CENTER
        typeface = Typeface.create(Typeface.DEFAULT, Typeface.BOLD)
    }
    private val fillPaint = Paint(Paint.ANTI_ALIAS_FLAG)
    private val shadowPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        maskFilter = BlurMaskFilter(6f * resources.displayMetrics.density, BlurMaskFilter.Blur.NORMAL)
    }
    private val highlightPaint = Paint(Paint.ANTI_ALIAS_FLAG)

    private val conflictPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#E53935")
        strokeWidth = 3.5f * resources.displayMetrics.density
    }

    private val palette = intArrayOf(
        Color.parseColor("#EF5350"), // 1
        Color.parseColor("#AB47BC"), // 2
        Color.parseColor("#5C6BC0"), // 3
        Color.parseColor("#29B6F6"), // 4
        Color.parseColor("#26A69A"), // 5
        Color.parseColor("#66BB6A"), // 6
        Color.parseColor("#9CCC65"), // 7
        Color.parseColor("#D4E157"), // 8
        Color.parseColor("#FFCA28"), // 9
        Color.parseColor("#FFA726"), // 10
        Color.parseColor("#8D6E63"), // 11
        Color.parseColor("#EC407A"), // 12
        Color.parseColor("#26C6DA"), // 13
        Color.parseColor("#7E57C2"), // 14
        Color.parseColor("#42A5F5"), // 15
        Color.parseColor("#8E24AA"), // 16
        Color.parseColor("#66A6FF"), // 17
        Color.parseColor("#00BFA5"), // 18
        Color.parseColor("#7CB342"), // 19
        Color.parseColor("#C0CA33"), // 20
        Color.parseColor("#FFB300"), // 21
        Color.parseColor("#FB8C00"), // 22
        Color.parseColor("#6D4C41"), // 23
        Color.parseColor("#F06292")  // 24
    )

    fun startNewGame(size: Int) {
        gridSize = size
        board = Array(gridSize) { r -> IntArray(gridSize) { c -> 1 + (r + c) % gridSize } }
        conflictsRow = Array(gridSize) { BooleanArray(gridSize) }
        conflictsCol = Array(gridSize) { BooleanArray(gridSize) }
        // Небольшая случайная перестановка, чтобы старт был не сразу решён
        repeat(gridSize * 2) {
            val r = (0 until gridSize).random()
            val c = (0 until gridSize).random()
            val steps = (1..gridSize).random()
            board[r][c] = ((board[r][c] - 1 + steps) % gridSize) + 1
        }
        // Гарантия: стартовое поле не должно быть решённым
        var guard = 0
        while (isSolved() && guard < 100) {
            val r = (0 until gridSize).random()
            val c = (0 until gridSize).random()
            board[r][c] = (board[r][c] % gridSize) + 1
            guard++
        }
        flipping = false; flipAnimator?.cancel(); flipAnimator = null
        updateConflicts()
        invalidate()
    }

    override fun onSizeChanged(w: Int, h: Int, oldw: Int, oldh: Int) {
        super.onSizeChanged(w, h, oldw, oldh)
        val cw = w - paddingLeft - paddingRight
        val ch = h - paddingTop - paddingBottom
        cellSize = min(cw / gridSize.toFloat(), ch / gridSize.toFloat())
        val gridW = cellSize * gridSize
        val gridH = cellSize * gridSize
        gridLeft = paddingLeft + (cw - gridW) / 2f
        gridTop = paddingTop + (ch - gridH) / 2f
        textPaint.textSize = cellSize * 0.5f
    }

    override fun onDraw(canvas: Canvas) {
        super.onDraw(canvas)
        for (r in 0 until gridSize) for (c in 0 until gridSize) {
            val l = gridLeft + c * cellSize
            val t = gridTop + r * cellSize
            val isConf = conflictsRow[r][c] || conflictsCol[r][c]
            drawCell(canvas, l, t, r, c, isConf)
        }

        // Тонкая сетка
        for (i in 0..gridSize) {
            val x = gridLeft + i * cellSize
            val y = gridTop + i * cellSize
            canvas.drawLine(gridLeft, y, gridLeft + cellSize * gridSize, y, thinPaint)
            canvas.drawLine(x, gridTop, x, gridTop + cellSize * gridSize, thinPaint)
        }

        // Жирные границы 2x2 блоков (чётные N) или только внешняя рамка для 3x3
        if (gridSize % 2 == 0) {
            val mid = gridSize / 2
            val x = gridLeft + mid * cellSize
            val y = gridTop + mid * cellSize
            canvas.drawRect(gridLeft, gridTop, gridLeft + gridSize * cellSize, gridTop + gridSize * cellSize, boldPaint)
            canvas.drawLine(x, gridTop, x, gridTop + gridSize * cellSize, boldPaint)
            canvas.drawLine(gridLeft, y, gridLeft + gridSize * cellSize, y, boldPaint)
        } else {
            canvas.drawRect(gridLeft, gridTop, gridLeft + gridSize * cellSize, gridTop + gridSize * cellSize, boldPaint)
        }
    }

    private fun drawCell(canvas: Canvas, left: Float, top: Float, r: Int, c: Int, isConflict: Boolean) {
        val size = cellSize * 0.9f
        val round = cellSize * 0.18f
        val l = left + (cellSize - size) / 2f
        val t = top + (cellSize - size) / 2f
        val rgt = l + size
        val btm = t + size

        // Определяем показываемое значение (для переворота до середины — старое)
        val showingValue = if (flipping && r == flipR && c == flipC && flipProgress < 0.5f) flipPrevValue else board[r][c]
        val baseColor = palette[(showingValue - 1) % palette.size]

        // Тень
        shadowPaint.color = baseColor
        canvas.drawRoundRect(l + 3f, t + 3f, rgt + 3f, btm + 3f, round, round, shadowPaint)

        // Эффект переворота ��о оси X (вертикальный переворот)
        val scale = if (flipping && r == flipR && c == flipC) {
            val s = abs(cos(Math.PI.toFloat() * flipProgress)).coerceAtLeast(0.06f)
            s
        } else 1f

        canvas.save()
        val cx = (l + rgt) / 2f
        val cy = (t + btm) / 2f
        canvas.translate(cx, cy)
        canvas.scale(1f, scale)
        canvas.translate(-cx, -cy)

        // Корпус «кубика» с глянцем
        fillPaint.shader = LinearGradient(
            l, t, rgt, btm,
            lighten(baseColor, 0.25f), darken(baseColor, 0.18f), Shader.TileMode.CLAMP
        )
        canvas.drawRoundRect(l, t, rgt, btm, round, round, fillPaint)

        highlightPaint.shader = LinearGradient(
            l, t, l, t + size * 0.45f,
            Color.argb(140, 255, 255, 255), Color.TRANSPARENT,
            Shader.TileMode.CLAMP
        )
        val inset = size * 0.06f
        canvas.drawRoundRect(l + inset, t + inset, rgt - inset, t + size * 0.45f, round, round, highlightPaint)

        // Цифра
        textPaint.color = Color.WHITE
        val text = showingValue.toString()
        val fm = textPaint.fontMetrics
        val textY = cy - (fm.ascent + fm.descent) / 2f
        canvas.drawText(text, cx, textY, textPaint)

        canvas.restore()

        // Рамка конфликта (не меняем цвет ячейки, что��ы другие клетки визуально не «менялись»)
        if (isConflict) {
            canvas.drawRoundRect(l, t, rgt, btm, round, round, conflictPaint)
        }
    }

    override fun onTouchEvent(event: MotionEvent): Boolean {
        when (event.action) {
            MotionEvent.ACTION_DOWN -> return true
            MotionEvent.ACTION_UP -> {
                if (flipping) return true // во время анимации игнорируем клики
                val c = ((event.x - gridLeft) / cellSize).toInt()
                val r = ((event.y - gridTop) / cellSize).toInt()
                if (r in 0 until gridSize && c in 0 until gridSize) {
                    startFlip(r, c)
                    performClick()
                    return true
                }
            }
        }
        return super.onTouchEvent(event)
    }

    override fun performClick(): Boolean {
        super.performClick(); return true
    }

    private fun startFlip(r: Int, c: Int) {
        flipping = true
        flipR = r; flipC = c
        flipPrevValue = board[r][c]
        flipProgress = 0f
        flipAnimator?.cancel()
        flipAnimator = ValueAnimator.ofFloat(0f, 1f).apply {
            duration = 220L
            interpolator = AccelerateDecelerateInterpolator()
            addUpdateListener { va ->
                val p = (va.animatedValue as Float).coerceIn(0f, 1f)
                val midCrossed = flipProgress < 0.5f && p >= 0.5f
                flipProgress = p
                if (midCrossed) {
                    // На середине переворота меняем значение клетки
                    board[r][c] = (board[r][c] % gridSize) + 1
                    updateConflicts()
                }
                invalidate()
            }
            // withEndAction недоступен для ValueAnimator; используем doOnEnd
            doOnEnd {
                flipping = false
                if (isSolved()) onSolved?.invoke()
            }
            start()
        }
    }

    private fun updateConflicts() {
        for (r in 0 until gridSize) for (c in 0 until gridSize) {
            conflictsRow[r][c] = false
            conflictsCol[r][c] = false
        }
        // строки
        for (r in 0 until gridSize) {
            val counts = HashMap<Int, MutableList<Int>>()
            for (c in 0 until gridSize) {
                counts.getOrPut(board[r][c]) { mutableListOf() }.add(c)
            }
            for ((_, cols) in counts) if (cols.size > 1) for (c in cols) conflictsRow[r][c] = true
        }
        // столбцы
        for (c in 0 until gridSize) {
            val counts = HashMap<Int, MutableList<Int>>()
            for (r in 0 until gridSize) {
                counts.getOrPut(board[r][c]) { mutableListOf() }.add(r)
            }
            for ((_, rows) in counts) if (rows.size > 1) for (r in rows) conflictsCol[r][c] = true
        }
    }

    private fun isSolved(): Boolean {
        val target = (1..gridSize).toSet()
        for (r in 0 until gridSize) {
            val rowSet = HashSet<Int>()
            for (c in 0 until gridSize) rowSet.add(board[r][c])
            if (rowSet != target) return false
        }
        for (c in 0 until gridSize) {
            val colSet = HashSet<Int>()
            for (r in 0 until gridSize) colSet.add(board[r][c])
            if (colSet != target) return false
        }
        return true
    }

    private fun lighten(color: Int, factor: Float): Int {
        val a = Color.alpha(color)
        val r = Color.red(color)
        val g = Color.green(color)
        val b = Color.blue(color)
        val nr = (r + (255 - r) * factor).roundToInt().coerceIn(0, 255)
        val ng = (g + (255 - g) * factor).roundToInt().coerceIn(0, 255)
        val nb = (b + (255 - b) * factor).roundToInt().coerceIn(0, 255)
        return Color.argb(a, nr, ng, nb)
    }

    private fun darken(color: Int, factor: Float): Int {
        val a = Color.alpha(color)
        val r = (Color.red(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        val g = (Color.green(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        val b = (Color.blue(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        return Color.argb(a, r, g, b)
    }
}

package com.timursarsembayev.danabalanumbers

import android.content.Context
import android.graphics.*
import android.util.AttributeSet
import android.view.MotionEvent
import android.view.View
import android.animation.ValueAnimator
import android.view.animation.AccelerateDecelerateInterpolator
import androidx.core.animation.doOnEnd
import kotlin.math.min
import kotlin.math.roundToInt
import kotlin.math.abs
import kotlin.math.cos
import kotlin.random.Random

class SudokuKidsView @JvmOverloads constructor(
    context: Context,
    attrs: AttributeSet? = null,
    defStyleAttr: Int = 0
) : View(context, attrs, defStyleAttr) {

    var onSolved: (() -> Unit)? = null

    var gridSize: Int = 3
        private set

    private var cellSize = 0f
    private var gridLeft = 0f
    private var gridTop = 0f

    private lateinit var board: Array<IntArray>
    private lateinit var conflictsRow: Array<BooleanArray>
    private lateinit var conflictsCol: Array<BooleanArray>

    // Анимация переворота для одной клетки за раз
    private var flipping = false
    private var flipR = -1
    private var flipC = -1
    private var flipPrevValue = 0
    private var flipProgress = 0f // 0..1
    private var flipAnimator: ValueAnimator? = null

    private val thinPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#33000000")
        strokeWidth = 1f * resources.displayMetrics.density
    }
    private val boldPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#66000000")
        strokeWidth = 3f * resources.displayMetrics.density
    }

    private val textPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        color = Color.WHITE
        textAlign = Paint.Align.CENTER
        typeface = Typeface.create(Typeface.DEFAULT, Typeface.BOLD)
    }
    private val fillPaint = Paint(Paint.ANTI_ALIAS_FLAG)
    private val shadowPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        maskFilter = BlurMaskFilter(6f * resources.displayMetrics.density, BlurMaskFilter.Blur.NORMAL)
    }
    private val highlightPaint = Paint(Paint.ANTI_ALIAS_FLAG)

    private val conflictPaint = Paint(Paint.ANTI_ALIAS_FLAG).apply {
        style = Paint.Style.STROKE
        color = Color.parseColor("#E53935")
        strokeWidth = 3.5f * resources.displayMetrics.density
    }

    // Диапазон допустимых значений в текущем уровне: [valueStart .. valueEnd]
    private var valueStart = 1
    private var valueEnd = 3

    // Шаг подрешёток для жирных линий (0 — без внутренних линий)
    private var subStep = 0

    // Палитра для цифр 0..9 (жёсткое соответствие: цифра -> цвет)
    private val palette10 = intArrayOf(
        Color.parseColor("#FF5252"), // 0
        Color.parseColor("#FF9800"), // 1
        Color.parseColor("#FFEB3B"), // 2
        Color.parseColor("#4CAF50"), // 3
        Color.parseColor("#00BCD4"), // 4
        Color.parseColor("#3F51B5"), // 5
        Color.parseColor("#9C27B0"), // 6
        Color.parseColor("#795548"), // 7
        Color.parseColor("#8BC34A"), // 8
        Color.parseColor("#03A9F4")  // 9
    )

    fun startNewGame(size: Int) {
        gridSize = size
        // Выбираем диапазон подряд идущих цифр в 0..9 длиной gridSize
        val maxStart = 10 - gridSize
        valueStart = Random.nextInt(0, maxStart + 1)
        valueEnd = valueStart + gridSize - 1

        // Подрешётки: выбираем шаг
        subStep = when (gridSize) {
            3 -> 0
            4 -> 2
            6 -> if (Random.nextBoolean()) 3 else 2 // 4 блока 3x3 или 9 блоков 2x2
            8 -> if (Random.nextBoolean()) 4 else 2 // 4 блока 4x4 или 16 блоков 2x2
            9 -> 3
            else -> 0
        }

        board = Array(gridSize) { r -> IntArray(gridSize) { c -> valueStart + ((r + c) % gridSize) } }
        conflictsRow = Array(gridSize) { BooleanArray(gridSize) }
        conflictsCol = Array(gridSize) { BooleanArray(gridSize) }
        // Небольшая случайная перестановка, чтобы старт был не сразу решён
        repeat(gridSize * 2) {
            val r = (0 until gridSize).random()
            val c = (0 until gridSize).random()
            val steps = (1..gridSize).random()
            val base = board[r][c]
            val offset = (base - valueStart + steps) % gridSize
            board[r][c] = valueStart + offset
        }
        // Гарантия: стартовое поле не должно быть решённым
        var guard = 0
        while (isSolved() && guard < 100) {
            val r = (0 until gridSize).random()
            val c = (0 until gridSize).random()
            board[r][c] = if (board[r][c] < valueEnd) board[r][c] + 1 else valueStart
            guard++
        }
        flipping = false; flipAnimator?.cancel(); flipAnimator = null
        updateConflicts()
        invalidate()
    }

    override fun onSizeChanged(w: Int, h: Int, oldw: Int, oldh: Int) {
        super.onSizeChanged(w, h, oldw, oldh)
        val cw = w - paddingLeft - paddingRight
        val ch = h - paddingTop - paddingBottom
        cellSize = min(cw / gridSize.toFloat(), ch / gridSize.toFloat())
        val gridW = cellSize * gridSize
        val gridH = cellSize * gridSize
        gridLeft = paddingLeft + (cw - gridW) / 2f
        gridTop = paddingTop + (ch - gridH) / 2f
        textPaint.textSize = cellSize * 0.5f
    }

    override fun onDraw(canvas: Canvas) {
        super.onDraw(canvas)
        for (r in 0 until gridSize) for (c in 0 until gridSize) {
            val l = gridLeft + c * cellSize
            val t = gridTop + r * cellSize
            val isConf = conflictsRow[r][c] || conflictsCol[r][c]
            drawCell(canvas, l, t, r, c, isConf)
        }

        // Тонкая сетка
        for (i in 0..gridSize) {
            val x = gridLeft + i * cellSize
            val y = gridTop + i * cellSize
            canvas.drawLine(gridLeft, y, gridLeft + cellSize * gridSize, y, thinPaint)
            canvas.drawLine(x, gridTop, x, gridTop + cellSize * gridSize, thinPaint)
        }

        // Внешняя рамка и жирные линии по шагу subStep
        canvas.drawRect(gridLeft, gridTop, gridLeft + gridSize * cellSize, gridTop + gridSize * cellSize, boldPaint)
        if (subStep > 0) {
            var k = subStep
            while (k < gridSize) {
                val x = gridLeft + k * cellSize
                val y = gridTop + k * cellSize
                canvas.drawLine(x, gridTop, x, gridTop + gridSize * cellSize, boldPaint)
                canvas.drawLine(gridLeft, y, gridLeft + gridSize * cellSize, y, boldPaint)
                k += subStep
            }
        }
    }

    private fun drawCell(canvas: Canvas, left: Float, top: Float, r: Int, c: Int, isConflict: Boolean) {
        val size = cellSize * 0.9f
        val round = cellSize * 0.18f
        val l = left + (cellSize - size) / 2f
        val t = top + (cellSize - size) / 2f
        val rgt = l + size
        val btm = t + size

        // Определяем показываемое значение (для переворота до середины — старое)
        val showingValue = if (flipping && r == flipR && c == flipC && flipProgress < 0.5f) flipPrevValue else board[r][c]
        val baseColor = palette10[showingValue]

        // Тень
        shadowPaint.color = baseColor
        canvas.drawRoundRect(l + 3f, t + 3f, rgt + 3f, btm + 3f, round, round, shadowPaint)

        // Эффект переворота ��о оси X (вертикальный переворот)
        val scale = if (flipping && r == flipR && c == flipC) {
            val s = abs(cos(Math.PI.toFloat() * flipProgress)).coerceAtLeast(0.06f)
            s
        } else 1f

        canvas.save()
        val cx = (l + rgt) / 2f
        val cy = (t + btm) / 2f
        canvas.translate(cx, cy)
        canvas.scale(1f, scale)
        canvas.translate(-cx, -cy)

        // Корпус «кубика» с глянцем
        fillPaint.shader = LinearGradient(
            l, t, rgt, btm,
            lighten(baseColor, 0.25f), darken(baseColor, 0.18f), Shader.TileMode.CLAMP
        )
        canvas.drawRoundRect(l, t, rgt, btm, round, round, fillPaint)

        highlightPaint.shader = LinearGradient(
            l, t, l, t + size * 0.45f,
            Color.argb(140, 255, 255, 255), Color.TRANSPARENT,
            Shader.TileMode.CLAMP
        )
        val inset = size * 0.06f
        canvas.drawRoundRect(l + inset, t + inset, rgt - inset, t + size * 0.45f, round, round, highlightPaint)

        // Цифра
        textPaint.color = Color.WHITE
        val text = showingValue.toString()
        val fm = textPaint.fontMetrics
        val textY = cy - (fm.ascent + fm.descent) / 2f
        canvas.drawText(text, cx, textY, textPaint)

        canvas.restore()

        // Рамка конфликта (не меняем цвет ячейки, что��ы другие клетки визуально не «менялись»)
        if (isConflict) {
            canvas.drawRoundRect(l, t, rgt, btm, round, round, conflictPaint)
        }
    }

    override fun onTouchEvent(event: MotionEvent): Boolean {
        when (event.action) {
            MotionEvent.ACTION_DOWN -> return true
            MotionEvent.ACTION_UP -> {
                if (flipping) return true // во время анимации игнорируем клики
                val c = ((event.x - gridLeft) / cellSize).toInt()
                val r = ((event.y - gridTop) / cellSize).toInt()
                if (r in 0 until gridSize && c in 0 until gridSize) {
                    startFlip(r, c)
                    performClick()
                    return true
                }
            }
        }
        return super.onTouchEvent(event)
    }

    override fun performClick(): Boolean {
        super.performClick(); return true
    }

    private fun startFlip(r: Int, c: Int) {
        flipping = true
        flipR = r; flipC = c
        flipPrevValue = board[r][c]
        flipProgress = 0f
        flipAnimator?.cancel()
        flipAnimator = ValueAnimator.ofFloat(0f, 1f).apply {
            duration = 220L
            interpolator = AccelerateDecelerateInterpolator()
            addUpdateListener { va ->
                val p = (va.animatedValue as Float).coerceIn(0f, 1f)
                val midCrossed = flipProgress < 0.5f && p >= 0.5f
                flipProgress = p
                if (midCrossed) {
                    // На середине переворота меняем значение клетки в пределах [valueStart..valueEnd]
                    val cur = board[r][c]
                    board[r][c] = if (cur < valueEnd) cur + 1 else valueStart
                    updateConflicts()
                }
                invalidate()
            }
            // withEndAction недоступен для ValueAnimator; используем doOnEnd
            doOnEnd {
                flipping = false
                if (isSolved()) onSolved?.invoke()
            }
            start()
        }
    }

    private fun updateConflicts() {
        for (r in 0 until gridSize) for (c in 0 until gridSize) {
            conflictsRow[r][c] = false
            conflictsCol[r][c] = false
        }
        // строки
        for (r in 0 until gridSize) {
            val counts = HashMap<Int, MutableList<Int>>()
            for (c in 0 until gridSize) {
                counts.getOrPut(board[r][c]) { mutableListOf() }.add(c)
            }
            for ((_, cols) in counts) if (cols.size > 1) for (c in cols) conflictsRow[r][c] = true
        }
        // столбцы
        for (c in 0 until gridSize) {
            val counts = HashMap<Int, MutableList<Int>>()
            for (r in 0 until gridSize) {
                counts.getOrPut(board[r][c]) { mutableListOf() }.add(r)
            }
            for ((_, rows) in counts) if (rows.size > 1) for (r in rows) conflictsCol[r][c] = true
        }
    }

    private fun isSolved(): Boolean {
        val target = (valueStart..valueEnd).toSet()
        for (r in 0 until gridSize) {
            val rowSet = HashSet<Int>()
            for (c in 0 until gridSize) rowSet.add(board[r][c])
            if (rowSet != target) return false
        }
        for (c in 0 until gridSize) {
            val colSet = HashSet<Int>()
            for (r in 0 until gridSize) colSet.add(board[r][c])
            if (colSet != target) return false
        }
        return true
    }

    private fun lighten(color: Int, factor: Float): Int {
        val a = Color.alpha(color)
        val r = Color.red(color)
        val g = Color.green(color)
        val b = Color.blue(color)
        val nr = (r + (255 - r) * factor).roundToInt().coerceIn(0, 255)
        val ng = (g + (255 - g) * factor).roundToInt().coerceIn(0, 255)
        val nb = (b + (255 - b) * factor).roundToInt().coerceIn(0, 255)
        return Color.argb(a, nr, ng, nb)
    }

    private fun darken(color: Int, factor: Float): Int {
        val a = Color.alpha(color)
        val r = (Color.red(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        val g = (Color.green(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        val b = (Color.blue(color) * (1f - factor)).roundToInt().coerceIn(0, 255)
        return Color.argb(a, r, g, b)
    }
}

package com.timursarsembayev.danabalanumbers

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.cardview.widget.CardView
import androidx.recyclerview.widget.RecyclerView

class AudioButtonsAdapter(
    private var items: MutableList<MatchingItem>,
    private val onItemClick: (MatchingItem, View) -> Unit
) : RecyclerView.Adapter<AudioButtonsAdapter.AudioButtonViewHolder>() {

    class AudioButtonViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val cardView: CardView = itemView.findViewById(R.id.cardView)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AudioButtonViewHolder {
        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_audio_button, parent, false)
        return AudioButtonViewHolder(view)
    }

    override fun onBindViewHolder(holder: AudioButtonViewHolder, position: Int) {
        val item = items[position]

        // Устанавливаем прозрачность для сопоставленных элементов
        holder.cardView.alpha = if (item.isMatched) 0.3f else 1.0f

        holder.cardView.setOnClickListener {
            if (!item.isMatched) {
                // Простой эффект нажатия
                holder.cardView.animate().scaleX(0.95f).scaleY(0.95f).setDuration(100)
                    .withEndAction {
                        holder.cardView.animate().scaleX(1.0f).scaleY(1.0f).setDuration(100).start()
                    }.start()

                onItemClick(item, holder.cardView)
            }
        }
    }

    override fun getItemCount(): Int = items.size

    fun updateItems(newItems: List<MatchingItem>) {
        items.clear()
        items.addAll(newItems)
        notifyDataSetChanged()
    }

    fun removeItem(item: MatchingItem) {
        val position = items.indexOf(item)
        if (position != -1) {
            items.removeAt(position)
            notifyItemRemoved(position)
        }
    }
}

package com.timursarsembayev.danabalanumbers

import android.animation.Animator
import android.animation.AnimatorListenerAdapter
import android.animation.ValueAnimator
import android.content.Intent
import android.os.Bundle
import android.speech.tts.TextToSpeech
import android.view.View
import android.widget.ImageView
import android.widget.LinearLayout
import android.widget.ProgressBar
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import java.util.Locale
import kotlin.random.Random

class AudioMatchingActivity : AppCompatActivity(), TextToSpeech.OnInitListener {

    private lateinit var audioButtonsRecyclerView: RecyclerView
    private lateinit var numbersRecyclerView: RecyclerView
    private lateinit var audioButtonsAdapter: AudioButtonsAdapter
    private lateinit var numbersAdapter: NumbersAdapter
    private lateinit var progressBar: ProgressBar
    private lateinit var backButton: ImageView

    private var currentLevel = 1
    private var totalLevels = 10
    private var levels = mutableListOf<AudioMatchingLevel>()
    private var currentLevelData: AudioMatchingLevel? = null

    private var selectedAudioItem: MatchingItem? = null
    private var selectedNumberItem: MatchingItem? = null
    private var selectedAudioView: View? = null
    private var selectedNumberView: View? = null

    private var completedMatches = 0
    private var correctActions = 0
    private var incorrectActions = 0

    private lateinit var tts: TextToSpeech
    private var isTtsReady = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_audio_matching)

        initTTS()
        initViews()
        initData()
        setupRecyclerViews()
        loadLevel(currentLevel)
    }

    private fun initTTS() {
        tts = TextToSpeech(this, this)
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            val result = tts.setLanguage(Locale("ru"))
            isTtsReady = result != TextToSpeech.LANG_MISSING_DATA && result != TextToSpeech.LANG_NOT_SUPPORTED
        }
    }

    private fun speakNumber(number: Int) {
        if (isTtsReady) {
            val numberText = when (number) {
                1 -> "один"
                2 -> "два"
                3 -> "три"
                4 -> "четыре"
                5 -> "пять"
                6 -> "шесть"
                7 -> "семь"
                8 -> "восемь"
                9 -> "девять"
                else -> number.toString()
            }
            tts.speak(numberText, TextToSpeech.QUEUE_FLUSH, null, null)
        }
    }

    private fun speakText(text: String) {
        if (isTtsReady) {
            tts.speak(text, TextToSpeech.QUEUE_FLUSH, null, null)
        }
    }

    private fun initViews() {
        audioButtonsRecyclerView = findViewById(R.id.audioButtonsRecyclerView)
        numbersRecyclerView = findViewById(R.id.numbersRecyclerView)
        progressBar = findViewById(R.id.progressBar)
        backButton = findViewById(R.id.backButton)

        backButton.setOnClickListener {
            finish()
        }
    }

    private fun initData() {
        levels = AudioMatchingGameData.generateAllLevels().toMutableList()
    }

    private fun setupRecyclerViews() {
        audioButtonsAdapter = AudioButtonsAdapter(mutableListOf()) { item, view ->
            onAudioButtonClick(item, view)
        }

        numbersAdapter = NumbersAdapter(mutableListOf()) { item, view ->
            onNumberItemClick(item, view)
        }

        audioButtonsRecyclerView.apply {
            layoutManager = LinearLayoutManager(this@AudioMatchingActivity)
            adapter = audioButtonsAdapter
        }

        numbersRecyclerView.apply {
            layoutManager = LinearLayoutManager(this@AudioMatchingActivity)
            adapter = numbersAdapter
        }
    }

    private fun loadLevel(level: Int) {
        currentLevelData = levels[level - 1]
        updateProgressBar()
        completedMatches = 0

        // Перемешиваем элементы для случайного порядка
        val audioButtons = currentLevelData!!.pairs.map { it.number }.shuffled()
        val numbers = currentLevelData!!.pairs.map { it.number }.shuffled()

        audioButtonsAdapter.updateItems(audioButtons)
        numbersAdapter.updateItems(numbers)

        clearSelections()
        resetViewsAlpha()
    }

    private fun updateProgressBar() {
        val progress = (currentLevel * 100) / totalLevels
        progressBar.progress = progress
    }

    private fun resetViewsAlpha() {
        audioButtonsRecyclerView.post {
            for (i in 0 until audioButtonsRecyclerView.childCount) {
                val child = audioButtonsRecyclerView.getChildAt(i)
                child.alpha = 1.0f
                child.scaleX = 1.0f
                child.scaleY = 1.0f
            }
        }

        numbersRecyclerView.post {
            for (i in 0 until numbersRecyclerView.childCount) {
                val child = numbersRecyclerView.getChildAt(i)
                child.alpha = 1.0f
                child.scaleX = 1.0f
                child.scaleY = 1.0f
            }
        }
    }

    private fun onAudioButtonClick(item: MatchingItem, view: View) {
        if (item.isMatched) return

        // Озвучиваем число
        speakNumber(item.value)

        // Убираем предыдущее выделение
        selectedAudioView?.let { prevView ->
            val linearLayout = prevView.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.white))
        }

        if (selectedAudioItem == item) {
            clearSelections()
            return
        }

        selectedAudioItem = item
        selectedAudioView = view

        // Выделяем элемент зеленым цветом
        val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
        linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))

        checkMatch()
    }

    private fun onNumberItemClick(item: MatchingItem, view: View) {
        if (item.isMatched) return

        // Убираем предыдущее выделение
        selectedNumberView?.let { prevView ->
            val numberText = prevView.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, R.color.button_color))
        }

        if (selectedNumberItem == item) {
            clearSelections()
            return
        }

        selectedNumberItem = item
        selectedNumberView = view

        // Выделяем элемент зеленым цветом
        val numberText = view.findViewById<TextView>(R.id.numberText)
        numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))

        checkMatch()
    }

    private fun checkMatch() {
        val audioItem = selectedAudioItem
        val numberItem = selectedNumberItem

        if (audioItem != null && numberItem != null) {
            if (audioItem.value == numberItem.value) {
                onCorrectMatch(audioItem, numberItem)
            } else {
                onIncorrectMatch()
            }
        }
    }

    private fun onCorrectMatch(audioItem: MatchingItem, numberItem: MatchingItem) {
        correctActions++

        val randomPhrase = MatchingFeedbackPhrases.correctPhrases[Random.nextInt(MatchingFeedbackPhrases.correctPhrases.size)]
        speakText(randomPhrase)

        audioItem.isMatched = true
        numberItem.isMatched = true

        animateMatch(selectedAudioView!!, selectedNumberView!!) {
            audioButtonsAdapter.removeItem(audioItem)
            numbersAdapter.removeItem(numberItem)

            completedMatches++

            if (completedMatches >= 5) {
                onLevelCompleted()
            }
        }

        clearSelections()
    }

    private fun onIncorrectMatch() {
        incorrectActions++

        val randomPhrase = MatchingFeedbackPhrases.incorrectPhrases[Random.nextInt(MatchingFeedbackPhrases.incorrectPhrases.size)]
        speakText(randomPhrase)

        // Выделяем обе карточки красным цветом на полсекунды
        selectedAudioView?.let { view ->
            val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_red_light))
        }

        selectedNumberView?.let { view ->
            val numberText = view.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_red_light))
        }

        // Через 500ms возвращаем зеленое выделение
        android.os.Handler(android.os.Looper.getMainLooper()).postDelayed({
            selectedAudioView?.let { view ->
                val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
                linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))
            }

            selectedNumberView?.let { view ->
                val numberText = view.findViewById<TextView>(R.id.numberText)
                numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.holo_green_light))
            }
        }, 500)

        animateIncorrectMatch(selectedAudioView!!, selectedNumberView!!)
    }

    private fun animateMatch(view1: View, view2: View, onComplete: () -> Unit) {
        val animator1 = ValueAnimator.ofFloat(1f, 0f)
        val animator2 = ValueAnimator.ofFloat(1f, 0f)

        animator1.duration = 300
        animator2.duration = 300

        animator1.addUpdateListener { animation ->
            val alpha = animation.animatedValue as Float
            view1.alpha = alpha
            view1.scaleX = alpha
            view1.scaleY = alpha
        }

        animator2.addUpdateListener { animation ->
            val alpha = animation.animatedValue as Float
            view2.alpha = alpha
            view2.scaleX = alpha
            view2.scaleY = alpha
        }

        animator1.addListener(object : AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: Animator) {
                onComplete()
            }
        })

        animator1.start()
        animator2.start()
    }

    private fun animateIncorrectMatch(view1: View, view2: View) {
        val shake = ValueAnimator.ofFloat(0f, 25f, -25f, 25f, -25f, 15f, -15f, 6f, -6f, 0f)
        shake.duration = 600

        shake.addUpdateListener { animation ->
            val translateX = animation.animatedValue as Float
            view1.translationX = translateX
            view2.translationX = translateX
        }

        shake.addListener(object : AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: Animator) {
                view1.translationX = 0f
                view2.translationX = 0f
            }
        })

        shake.start()
    }

    private fun clearSelections() {
        selectedAudioItem = null
        selectedNumberItem = null

        selectedAudioView?.let { view ->
            val linearLayout = view.findViewById<LinearLayout>(R.id.audioButtonLayout)
            linearLayout?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, android.R.color.white))
        }

        selectedNumberView?.let { view ->
            val numberText = view.findViewById<TextView>(R.id.numberText)
            numberText?.setBackgroundColor(androidx.core.content.ContextCompat.getColor(this, R.color.button_color))
        }

        selectedAudioView = null
        selectedNumberView = null
    }

    private fun onLevelCompleted() {
        if (currentLevel < totalLevels) {
            currentLevel++
            loadLevel(currentLevel)
        } else {
            showResults()
        }
    }

    private fun showResults() {
        val finalScore = (correctActions * 10) - (incorrectActions * 5)

        val intent = Intent(this, AudioMatchingResultsActivity::class.java)
        intent.putExtra("completed_levels", currentLevel)
        intent.putExtra("correct_actions", correctActions)
        intent.putExtra("incorrect_actions", incorrectActions)
        intent.putExtra("final_score", finalScore)
        startActivity(intent)
        finish()
    }

    override fun onDestroy() {
        if (::tts.isInitialized) {
            tts.stop()
            tts.shutdown()
        }
        super.onDestroy()
    }
}

package com.timursarsembayev.danabalanumbers

data class AudioMatchingLevel(
    val pairs: List<AudioMatchingPair>
)

data class AudioMatchingPair(
    val number: MatchingItem // Используем ту же модель, что и в обычном сопоставлении
)

object AudioMatchingGameData {
    fun generateAllLevels(): List<AudioMatchingLevel> {
        val levels = mutableListOf<AudioMatchingLevel>()

        // Создаем 10 уровней
        repeat(10) { levelIndex ->
            val pairs = mutableListOf<AudioMatchingPair>()

            // Генерируем 5 пар чисел для каждого уровня
            val usedNumbers = mutableSetOf<Int>()
            repeat(5) {
                var number: Int
                do {
                    number = (1..9).random()
                } while (usedNumbers.contains(number))

                usedNumbers.add(number)

                val matchingItem = MatchingItem(
                    value = number,
                    emoji = "", // Для аудио кнопок не нужен emoji
                    isMatched = false
                )

                pairs.add(AudioMatchingPair(matchingItem))
            }

            levels.add(AudioMatchingLevel(pairs))
        }

        return levels
    }
}

package com.timursarsembayev.danabalanumbers

import android.content.Intent
import android.os.Bundle
import android.speech.tts.TextToSpeech
import android.widget.ImageView
import android.widget.TextView
import androidx.appcompat.app.AppCompatActivity
import androidx.cardview.widget.CardView
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import java.util.*
import kotlin.random.Random

class AudioMatchingResultsActivity : AppCompatActivity(), TextToSpeech.OnInitListener {

    private var tts: TextToSpeech? = null
    private var isTtsReady = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_audio_matching_results)

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main)) { v, insets ->
            val systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars())
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom)
            insets
        }

        // Инициализация TTS
        tts = TextToSpeech(this, this)

        setupViews()
        setupButtons()
    }

    override fun onInit(status: Int) {
        if (status == TextToSpeech.SUCCESS) {
            val result = tts?.setLanguage(Locale("ru"))
            isTtsReady = result != TextToSpeech.LANG_MISSING_DATA && result != TextToSpeech.LANG_NOT_SUPPORTED

            if (isTtsReady) {
                speakCongratulation()
            }
        }
    }

    private fun setupViews() {
        val completedLevels = intent.getIntExtra("completed_levels", 10)
        val correctActions = intent.getIntExtra("correct_actions", 0)
        val incorrectActions = intent.getIntExtra("incorrect_actions", 0)
        val finalScore = intent.getIntExtra("final_score", 0)

        // Обновляем UI с результатами
        findViewById<TextView>(R.id.completedLevelsText).text = "Пройдено уровней: $completedLevels"
        findViewById<TextView>(R.id.correctActionsText).text = "Правильных действий: $correctActions"
        findViewById<TextView>(R.id.incorrectActionsText).text = "Ошибок: $incorrectActions"
        findViewById<TextView>(R.id.finalScoreText).text = "Итоговые очки: $finalScore"

        // Определяем сообщение в зависимости от результата
        val accuracy = if (correctActions + incorrectActions > 0) {
            (correctActions * 100) / (correctActions + incorrectActions)
        } else {
            100
        }

        val (message, encouragement) = when {
            accuracy >= 90 -> Pair("Превосходно! ", "Ты отлично слышишь и различаешь числа!")
            accuracy >= 70 -> Pair("Отлично! ", "Ты хорошо сопоставляешь звуки с цифрами!")
            accuracy >= 50 -> Pair("Хорошо! ", "Продолжай тренироваться!")
            else -> Pair("Не сдавайся! ", "С каждым разом будет лучше!")
        }

        findViewById<TextView>(R.id.resultMessage).text = message
        findViewById<TextView>(R.id.encouragementText).text = encouragement
    }

    private fun speakCongratulation() {
        if (isTtsReady) {
            val correctActions = intent.getIntExtra("correct_actions", 0)
            val incorrectActions = intent.getIntExtra("incorrect_actions", 0)

            val accuracy = if (correctActions + incorrectActions > 0) {
                (correctActions * 100) / (correctActions + incorrectActions)
            } else {
                100
            }

            val phrases = when {
                accuracy == 100 -> DifferentiatedCongratulationPhrases.perfect100Phrases
                accuracy >= 90 -> DifferentiatedCongratulationPhrases.excellent90Phrases
                accuracy >= 80 -> DifferentiatedCongratulationPhrases.good80Phrases
                else -> DifferentiatedCongratulationPhrases.encouragement80Phrases
            }

            val randomPhrase = phrases[Random.nextInt(phrases.size)]
            tts?.speak(randomPhrase, TextToSpeech.QUEUE_FLUSH, null, "congratulation")
        }
    }

    private fun setupButtons() {
        findViewById<CardView>(R.id.btnPlayAgain).setOnClickListener {
            stopTTS()
            val intent = Intent(this, AudioMatchingActivity::class.java)
            startActivity(intent)
            finish()
        }

        findViewById<CardView>(R.id.btnBackToMenu).setOnClickListener {
            stopTTS()
            val intent = Intent(this, MathExercisesActivity::class.java)
            startActivity(intent)
            finish()
        }

        findViewById<ImageView>(R.id.btnBack).setOnClickListener {
            stopTTS()
            finish()
        }
    }

    private fun stopTTS() {
        tts?.stop()
    }

    override fun onDestroy() {
        tts?.stop()
        tts?.shutdown()
        super.onDestroy()
    }
}
